function formatChartData(apiData: Array<{date: string; value: number}>): { labels: string[]; data: number[][] } {
  const labels = apiData.map(item => item.date);
  const data = [apiData.map(item => item.value)];

  return { labels, data };
}

function aggregateByPeriod(data: Array<{date: Date; value: number}>, period: 'day' | 'week' | 'month'): Array<{date: string; value: number}> {
  const grouped = new Map();

  data.forEach(item => {
    let key: string;
    const date = new Date(item.date);

    switch (period) {
      case 'day':
        key = date.toISOString().split('T')[0];
        break;
      case 'week':
        const weekStart = new Date(date);
        weekStart.setDate(date.getDate() - date.getDay());
        key = weekStart.toISOString().split('T')[0];
        break;
      case 'month':
        key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        break;
    }

    if (!grouped.has(key)) {
      grouped.set(key, []);
    }
    grouped.get(key).push(item.value);
  });

  return Array.from(grouped.entries()).map(([date, values]) => ({
    date,
    value: values.reduce((sum, v) => sum + v, 0) / values.length
  }));
}

function calculatePercentageChange(data: number[]): number[] {
  return data.map((value, index) => {
    if (index === 0) return 0;
    const previous = data[index - 1];
    if (previous === 0) return 0;
    return ((value - previous) / previous) * 100;
  });
}

function generateColorPalette(count: number, baseHue: number = 200): string[] {
  const colors: string[] = [];
  const saturation = 70;
  const lightness = 50;

  for (let i = 0; i < count; i++) {
    const hue = (baseHue + (i * 360 / count)) % 360;
    colors.push(`hsl(${hue}, ${saturation}%, ${lightness}%)`);
  }

  return colors;
}

