  <InChips text="Neutral" value="4" type="neutral" />
  <InChips text="Smart" value="5" type="smart" />
  <InChips text="Information" value="6" type="information" />
  <InChips text="Orange" value="7" type="orange" />
  <InChips text="Magenta" value="8" type="magenta" />
</div>`,
    props: {
      text: 'Default',
      value: '1',
      type: 'default'
    },
    tags: ['advanced', 'colors', 'showcase']
  }
];

/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InChips text="Tag" />`,
    right: `<InChips text="Tag" value="tag-1" />`,
    reason: 'value prop is required for tracking and removal'
  },
  {
    wrong: `<InChips :text="undefined" value="1" />`,
    right: `<InChips text="Label" value="1" />`,
    reason: 'text prop is required and must be a non-empty string'
  },
  {
    wrong: `<InChips text="Tag" value="1" @click="handleClick()" />`,
    right: `<InChips text="Tag" value="1" @click="handleClick" />`,
    reason: 'Event handlers receive the value automatically, no need for ()'
  },
  {
    wrong: `<InChips text="Long text here" value="1" />`,
    right: `<InChips text="Long text here" value="1" :max-width="150" />`,
    reason: 'Use max-width to prevent layout issues with long text'
  },
  {
    wrong: `<InChips text="Tag" value="1" :interactive="false" :close-button="true" />`,
    right: `<InChips text="Tag" value="1" :interactive="false" :close-button="false" />`,
    reason: 'Close button is hidden when interactive is false'
  }
];

/**
 * Best practices for using InChips
 */
export const bestPractices = [
  {
    practice: 'Use semantic types for meaning',
    description: 'Choose type based on the semantic meaning of the tag',
    example: '<InChips text="Error" value="1" type="danger" />'
  },
  {
    practice: 'Set max-width for long text',
    description: 'Prevent layout breaks with truncation',
    example: '<InChips text="Long label" value="1" :max-width="150" />'
  },
  {
    practice: 'Use unique values',
    description: 'Ensure value prop is unique within the list for proper removal',
    example: 'value="tag-1" instead of value="1"'
  },
  {
    practice: 'Handle removal in parent',
    description: 'Manage chip list state in parent component',
    example: '@click="removeFromList" with proper state management'
  },
  {
    practice: 'Use actionable-label for navigation',
    description: 'Enable label clicks when chips represent navigable items',
    example: '<InChips :actionable-label="true" @tagClick="navigateToDetail" />'
  }
];

InChips.typeDefs:
import { DefineComponent } from 'vue';

export type ChipType = 'default' | 'danger' | 'warning' | 'neutral' | 'smart' | 'information' | 'orange' | 'magenta';
export type ChipStyle = 'stroke' | 'solid';

export interface InChipsProps {
  /**
   * Unique identifier for the chip.
   */
  id?: string;

  /**
   * Color type of the chip.
   * @default 'default'
   */
  type?: ChipType;

  /**
   * Visual style of the chip.
   * @default 'stroke'
   */
  styles?: ChipStyle;

  /**
   * Text content displayed in the chip.
   * @required
   */
  text: string;

  /**
   * Value associated with the chip.
   * Used in events and data attributes.
   * @required
   */
  value: string | number;

  /**
   * Disables the chip, preventing interaction.
   * @default false
   */
  disabledStatus?: boolean;

  /**
   * Makes the chip interactive (clickable).
   * @default true
   */
  interactive?: boolean;

  /**
   * Shows close (X) button on the chip.
   * @default true
   */
  closeButton?: boolean;

  /**
   * Marks the chip as selected.
   * @default false
   */
  selectedStatus?: boolean;

  /**
   * Makes the label text clickable (emits tagClick event).
   * @default false
   */
  actionableLabel?: boolean;

  /**
   * Maximum width of the chip in pixels.
   * Text will be truncated with ellipsis if exceeded.
   * @default null
   */
  maxWidth?: number;
}

declare const InChips: DefineComponent<InChipsProps>;
export default InChips;

