import { ref } from 'vue';
import { InChips } from '@useinsider/design-system-vue';

// Basic removal
const chips = ref([
  { text: 'Tag 1', value: '1' },
  { text: 'Tag 2', value: '2' },
  { text: 'Tag 3', value: '3' }
]);

const handleRemove = (value) => {
  chips.value = chips.value.filter(chip => chip.value !== value);
};

// Clickable label (navigation)
const navigateToDetails = (value) => {
  console.log('Navigate to:', value);
  // router.push(`/details/${value}`);
};

// Toggle selection
const selectedChips = ref(['1']);

const toggleSelection = (value) => {
  const index = selectedChips.value.indexOf(value);
  if (index > -1) {
    selectedChips.value.splice(index, 1);
  } else {
    selectedChips.value.push(value);
  }
};

const isChipSelected = (value) => {
  return selectedChips.value.includes(value);
};

// Filter chips by type
const filterChips = ref([
  { text: 'Active', value: 'active', type: 'smart' },
  { text: 'Inactive', value: 'inactive', type: 'neutral' },
  { text: 'Error', value: 'error', type: 'danger' }
]);

const activeFilters = ref(['active']);

const isFilterActive = (value) => {
  return activeFilters.value.includes(value);
};

const toggleFilter = (value) => {
  const index = activeFilters.value.indexOf(value);
  if (index > -1) {
    activeFilters.value.splice(index, 1);
  } else {
    activeFilters.value.push(value);
  }
};

