commonMistakes.example:
// WRONG - Critical XSS vulnerability
<InCodeSnippet :code="userCode" />

// CORRECT - XSS protected
<InCodeSnippet :code="userCode" :preventXss="true" />

// WRONG - conflicting settings
<InCodeSnippet :canEdit="true" :options="{ readOnly: true }" />

// CORRECT - consistent read-only
<InCodeSnippet :canEdit="false" :options="{ readOnly: true }" />

// CORRECT - consistent editable
<InCodeSnippet :canEdit="true" :options="{ readOnly: false }" />

// WRONG - no validation
<InCodeSnippet :canAddDynamicContent="true" />

// CORRECT - validates tags
<InCodeSnippet
  :canAddDynamicContent="true"
  :markNotExistDynamicContent="true"
  :dynamicContentBoxProps="availableTags"
  @markedTags="validateBeforeSave"
/>

// WRONG - ignores errors
const save = async (code) => {
  await api.save(code); // May fail silently
};

// CORRECT - handles errors
const save = async (code) => {
  try {
    await api.save(code);
    toast.success('Saved successfully');
  } catch (error) {
    toast.error('Save failed: ' + error.message);
    throw error; // Prevent editor from closing
  }
};

