import { ref, computed } from 'vue';

export function useCodeEditor(initialCode = '', options = {}) {
  const {
    onSave,
    onValidate,
    autoSave = false,
    autoSaveDelay = 2000
  } = options;

  const code = ref(initialCode);
  const originalCode = ref(initialCode);
  const syntaxErrors = ref([]);
  const saving = ref(false);
  const saveError = ref(null);

  const isDirty = computed(() => code.value !== originalCode.value);
  const hasErrors = computed(() => syntaxErrors.value.length > 0);
  const canSave = computed(() => isDirty.value && !hasErrors.value && !saving.value);

  let autoSaveTimer = null;

  const handleCodeChange = (newCode: string) => {
    code.value = newCode;
    saveError.value = null;

    if (autoSave && canSave.value) {
      clearTimeout(autoSaveTimer);
      autoSaveTimer = setTimeout(() => {
        save();
      }, autoSaveDelay);
    }
  };

  const handleSyntaxErrors = (errors: any[]) => {
    syntaxErrors.value = errors;
    if (onValidate) {
      onValidate(errors);
    }
  };

  const save = async () => {
    if (!canSave.value) return;

    saving.value = true;
    saveError.value = null;

    try {
      if (onSave) {
        await onSave(code.value);
      }
      originalCode.value = code.value;
      return true;
    } catch (error: any) {
      saveError.value = error.message;
      throw error;
    } finally {
      saving.value = false;
    }
  };

  const reset = () => {
    code.value = originalCode.value;
    syntaxErrors.value = [];
    saveError.value = null;
  };

  const setCode = (newCode: string) => {
    code.value = newCode;
    originalCode.value = newCode;
  };

  return {
    code,
    isDirty,
    hasErrors,
    canSave,
    saving,
    saveError,
    syntaxErrors,
    handleCodeChange,
    handleSyntaxErrors,
    save,
    reset,
