helperFunctions.code:
interface Attribute {
  name: string;
  label: string;
  type?: string;
  category?: string;
  description?: string;
}

export function createDynamicContentConfig(
  attributes: Attribute[],
  options: {
    groupBy?: string;
    searchable?: boolean;
    autoCategorizeFn?: (attr: Attribute) => string;
  } = {}
) {
  const { groupBy = 'category', searchable = true, autoCategorizeFn } = options;

  // Auto-categorize if function provided
  const categorizedAttributes = attributes.map(attr => {
    if (!attr.category && autoCategorizeFn) {
      return { ...attr, category: autoCategorizeFn(attr) };
    }
    return attr;
  });

  // Sort by category and then by label
  const sortedAttributes = [...categorizedAttributes].sort((a, b) => {
    const categoryCompare = (a.category || '').localeCompare(b.category || '');
    if (categoryCompare !== 0) return categoryCompare;
    return a.label.localeCompare(b.label);
  });

  return {
    contentObject: sortedAttributes.map(({ name, label, category, description }) => ({
      name,
      label,
      category,
      description
    })),
    groupBy,
    searchable
  };
}

// Auto-categorization helper
export function autoCategorizeBydotNotation(attr: Attribute): string {
  const parts = attr.name.split('.');
  if (parts.length > 1) {
    // Capitalize first part: 'user.name' -> 'User'
    return parts[0].charAt(0).toUpperCase() + parts[0].slice(1);
  }
  return 'Other';
}

