import { DefineComponent } from 'vue';

export interface InContainerProps {
  /**
   * Border styling classes for the container.
   * @default 'bor-w-1 bor-s-s bor-c-6'
   */
  border?: string;

  /**
   * Border radius styling class for the container.
   * @default 'bor-r-1'
   */
  borderRadius?: string;

  /**
   * Enables triangle decoration on container border.
   * @default false
   */
  borderTriangle?: boolean;

  /**
   * Position of the border triangle (top, right, bottom, left).
   * @default ''
   */
  borderTrianglePosition?: string;

  /**
   * Alignment of the border triangle (start, center, end).
   * @default ''
   */
  borderTriangleAlign?: string;

  /**
   * Shows the header section with slots.
   * @default false
   */
  headerStatus?: boolean;

  /**
   * Shows the footer section with slots.
   * @default false
   */
  footerStatus?: boolean;

  /**
   * Makes the content area full width without padding.
   * @default true
   */
  fullWidthContentStatus?: boolean;

  /**
   * Visual variant of the container.
   * @default 'default'
   */
  containerType?: string;

  /**
   * Shows a colored indicator bar on the left side.
   * @default false
   */
  colorIndicatorStatus?: boolean;

  /**
   * CSS color value for the color indicator.
   * @default ''
   */
  colorIndicatorColor?: string;
}

export interface InContainerSlots {
  /**
   * Main content area (default slot).
   */
  default?: any;

  /**
   * Content area slot (alternative to default).
   */
  contentArea?: any;

  /**
   * Header title content.
   */
  headerTitle?: any;

  /**
   * Header tag/badge content.
   */
  headerTag?: any;

  /**
   * Header description text.
   */
  headerDescription?: any;

  /**
   * Left side of header area.
   */
  headerLeftSlot?: any;

  /**
   * Right side of header area.
   */
  headerRightSlot?: any;

  /**
   * Bottom area below header.
   */
  headerBottomSlot?: any;

  /**
   * Footer title content.
   */
  footerTitle?: any;

  /**
   * Footer tag/badge content.
   */
  footerTag?: any;

  /**
   * Footer description text.
   */
  footerDescription?: any;

  /**
   * Left side of footer area.
   */
  footerLeftSlot?: any;

  /**
   * Right side of footer area.
   */
  footerRightSlot?: any;
}

declare const InContainer: DefineComponent<InContainerProps>;
export default InContainer;

