helperFunctions.code:
export function createOptionsFromArray<T>(
  items: T[],
  config: {
    labelKey?: keyof T | ((item: T) => string);
    valueKey?: keyof T | ((item: T) => any);
    iconKey?: keyof T | ((item: T) => string);
    disabledKey?: keyof T | ((item: T) => boolean);
  } = {}
) {
  const {
    labelKey = 'label',
    valueKey = 'value',
    iconKey,
    disabledKey
  } = config;

  return items.map(item => {
    const getProperty = (key: any) => {
      if (typeof key === 'function') {
        return key(item);
      }
      return item[key as keyof T];
    };

    const option: any = {
      label: getProperty(labelKey),
      value: getProperty(valueKey)
    };

    if (iconKey) {
      option.icon = getProperty(iconKey);
    }

    if (disabledKey) {
      option.disabled = getProperty(disabledKey);
    }

    return option;
  });
}

