import { ref, computed } from 'vue';

export function useDropdownState<T = any>(initialValue: T | null = null) {
  const isOpen = ref(false);
  const selectedValue = ref<T | null>(initialValue);
  const hoveredOption = ref<any>(null);

  const selectedOption = computed(() => {
    // Would be set from options array
    return null; // Placeholder
  });

  const open = () => {
    isOpen.value = true;
  };

  const close = () => {
    isOpen.value = false;
    hoveredOption.value = null;
  };

  const toggle = () => {
    isOpen.value = !isOpen.value;
  };

  const select = (option: any) => {
    selectedValue.value = option.value;
    close();
  };

  const clear = () => {
    selectedValue.value = null;
  };

  const handleHover = (option: any) => {
    hoveredOption.value = option;
  };

  return {
    isOpen,
    selectedValue,
    selectedOption,
    hoveredOption,
    open,
    close,
    toggle,
    select,
    clear,
    handleHover
  };
}

