helperFunctions.code:
function createTableField(field: string, label: string, options = {}) {
  return {
    field,
    label,
    sortable: false,
    width: 'auto',
    ...options
  };
}

function createSortableFields(fields: Array<{ field: string; label: string }>) {
  return fields.map(f => ({
    field: f.field,
    label: f.label,
    sortable: true
  }));
}

function filterTableData(data: any[], searchTerm: string, searchFields: string[]) {
  if (!searchTerm) return data;

  const term = searchTerm.toLowerCase();
  return data.filter(row => 
    searchFields.some(field => 
      String(row[field] || '').toLowerCase().includes(term)
    )
  );
}

function sortTableData(data: any[], sortBy: string, sortOrder: 'asc' | 'desc' = 'asc') {
  return [...data].sort((a, b) => {
    const aVal = a[sortBy];
    const bVal = b[sortBy];

    if (aVal === bVal) return 0;

    const comparison = aVal > bVal ? 1 : -1;
    return sortOrder === 'asc' ? comparison : -comparison;
  });
}

function paginateData(data: any[], page: number, perPage: number) {
  const start = (page - 1) * perPage;
  const end = start + perPage;

  return {
    data: data.slice(start, end),
    total: data.length,
    page,
    perPage,
    totalPages: Math.ceil(data.length / perPage)
  };
}

