const columns = ref([
  { field: 'id', label: 'ID', width: 80 },
  { field: 'name', label: 'Name', sortable: true },
  { field: 'email', label: 'Email', sortable: true },
  { field: 'createdAt', label: 'Created', sortable: true },
  { field: 'status', label: 'Status' }
]);

// Custom HTTP client with axios
const customHttpClient = async (url, options) => {
  try {
    const response = await axios.get(url, {
      params: options?.params,
      headers: options?.headers
    });
    // Convert axios response to fetch-like Response
    return new Response(JSON.stringify(response.data), {
      status: response.status,
      headers: response.headers
    });
  } catch (error) {
    throw new Error(`API Error: ${error.message}`);
  }
};

// Format functions
const formatDate = (value) => {
  if (!value) return '-';
  return new Date(value).toLocaleDateString();
};

const formatCurrency = (value) => {
  if (!value) return '$0.00';
  return `$${parseFloat(value).toFixed(2)}`;
};

const formatStatus = (value) => {
  const statusMap = {
    active: 'Active',
    inactive: 'Inactive',
    pending: 'Pending'
  };
  return statusMap[value] || value;
};

// Event handlers
const handleLoad = (payload) => {
  console.log('Data loaded:', payload.data.length, 'rows');
  console.log('Total:', payload.total);
};

const handleEdit = (row) => {
  console.log('Edit row:', row.id);
  // router.push(`/edit/${row.id}`);
};

const handleDelete = async (row) => {
  if (confirm(`Delete ${row.name}?`)) {
    // await api.delete(`/users/${row.id}`);
    console.log('Deleted:', row.id);
  }
};

const handleSelection = (payload) => {
  console.log('Selected rows:', payload.selectedRows);
  console.log('All selected:', payload.allSelected);
};

