/* InDataTable component styling */

/* Table wrapper */
.in-data-table {
  width: 100%;
  overflow-x: auto;
}

/* Table header */
.in-data-table__header {
  background-color: #f5f5f5;
  border-bottom: 2px solid #e0e0e0;
}

/* Table rows */
.in-data-table__row {
  border-bottom: 1px solid #e0e0e0;
  transition: background-color 0.2s;
}

.in-data-table__row:hover {
  background-color: #f9f9f9;
}

/* Custom row classes */
.bg-red-50 {
  background-color: #ffebee;
}

.error-row {
  background-color: #ffebee;
  color: #c62828;
}

.success-row {
  background-color: #e8f5e9;
}

/* Fixed columns */
.in-data-table--fixed-first .in-data-table__cell:first-child {
  position: sticky;
  left: 0;
  background-color: #fff;
  z-index: 10;
}

.in-data-table--fixed-last .in-data-table__cell:last-child {
  position: sticky;
  right: 0;
  background-color: #fff;
  z-index: 10;
}

/* Horizontal scroll */
.in-data-table--scroll {
  overflow-x: auto;
  max-width: 100%;
}

/* Pagination */
.in-data-table__pagination {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px;
  border-top: 1px solid #e0e0e0;
}

/* Loading skeleton */
.in-data-table__skeleton {
  animation: pulse 1.5s ease-in-out infinite;
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

