const authToken = ref('');
const httpOptions = computed(() => ({
  headers: {
    'Authorization': `Bearer ${authToken.value}`,
    'Content-Type': 'application/json'
  },
  credentials: 'include'
}));

<InDataTable
  api-mode
  fetch-url="/api/protected/users"
  :http-options="httpOptions"
  :table-fields="columns"
/>

const fetchWithRetry = async (url, options, retries = 3) => {
  for (let i = 0; i < retries; i++) {
    try {
      const response = await fetch(url, options);
      if (response.ok) return response;
      if (i === retries - 1) throw new Error('Max retries reached');
    } catch (error) {
      if (i === retries - 1) throw error;
      await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
    }
  }
};

<InDataTable
  api-mode
  fetch-url="/api/data"
  :http-fetch="fetchWithRetry"
  :table-fields="columns"
/>

const tableRef = ref(null);
const pollInterval = ref(null);

onMounted(() => {
  // Refresh table every 30 seconds
  pollInterval.value = setInterval(() => {
    if (tableRef.value) {
      tableRef.value.refresh(); // Call refresh method
    }
  }, 30000);
});

onUnmounted(() => {
  if (pollInterval.value) {
    clearInterval(pollInterval.value);
  }
});

<InDataTable
  ref="tableRef"
  api-mode
  fetch-url="/api/live-data"
  :table-fields="columns"
/>

const selectedRows = ref([]);

const exportToCSV = () => {
  if (selectedRows.value.length === 0) {
    alert('No rows selected');
    return;
  }

  const headers = columns.value.map(col => col.label);
  const rows = selectedRows.value.map(row => 
    columns.value.map(col => row[col.field])
  );

  const csv = [
    headers.join(','),
    ...rows.map(row => row.join(','))
  ].join('\n');

  const blob = new Blob([csv], { type: 'text/csv' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = 'export.csv';
  a.click();
};

<InDataTable
  :data="data"
  :table-fields="columns"
  @select-status="handleSelection"
/>
<InButtonV2 @click="exportToCSV" label-text="Export Selected" />

