/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InDataTableV2 :columns="columns" :data="data" />`,
    right: `<InDataTableV2 id="my-table" :columns="columns" :data="data" />`,
    reason: 'id prop is required for proper table initialization'
  },
  {
    wrong: `<InDataTableV2 id="table" :columns="[]" :data="data" />`,
    right: `<InDataTableV2 id="table" :columns="columns" :data="data" />`,
    reason: 'columns array must not be empty'
  },
  {
    wrong: `const columns = [{ label: 'Name' }];`,
    right: `const columns = [{ key: 'name', label: 'Name' }];`,
    reason: 'key property is required for each column'
  },
  {
    wrong: `<InDataTableV2 id="table" :columns="columns" :data="undefined" />`,
    right: `<InDataTableV2 id="table" :columns="columns" :data="[]" />`,
    reason: 'data must be an array, not undefined'
  },
  {
    wrong: `{ key: 'status', label: 'Status', width: '100' }`,
    right: `{ key: 'status', label: 'Status', width: '100px' }`,
    reason: 'width must include CSS units (px, %, etc.)'
  }
];

/**
 * Best practices for using InDataTableV2
 */
export const bestPractices = [
  {
    practice: 'Use descriptive IDs',
    description: 'Give tables unique, descriptive IDs for debugging',
    example: 'id="users-table" instead of id="table1"'
  },
  {
    practice: 'Enable pagination for large datasets',
    description: 'Use pagination when displaying more than 50 rows',
    example: ':pagination="true" :page-size="25"'
  },
  {
    practice: 'Define appropriate column widths',
    description: 'Set widths for important columns to prevent layout shifts',
    example: '{ key: "name", label: "Name", width: "200px" }'
  },
  {
    practice: 'Use cell renderers for complex data',
    description: 'Custom renderers for status badges, actions, etc.',
    example: 'cellRenderer: "InStatus" with cellRendererParams'
  },
  {
    practice: 'Pin action columns',
    description: 'Keep action buttons visible during horizontal scroll',
    example: '{ key: "actions", label: "Actions", pinned: "right" }'
  },
  {
    practice: 'Handle loading states',
    description: 'Always show loading indicator during data fetch',
    example: ':loading="isLoading" with proper async handling'
  }
];

InDataTableV2.typeDefs:
import { DefineComponent } from 'vue';

/**
 * Column definition for data table.
 */
export interface TableColumn {
  /**
   * Data property key to display in this column.
   * Maps to property name in row data objects.
   */
  key: string;

  /**
   * Column header label text.
   */
  label: string;

  /**
   * Enable sorting for this column.
   *
   * @default false
   */
  sortable?: boolean;

  /**
   * Column width (CSS value).
   *
   * @example width="120px"
   * @example width="20%"
   */
  width?: string;

  /**
   * Custom cell renderer component name.
   * Uses AG Grid's cell renderer framework.
   *
   * @example cellRenderer="InStatus"
   * @example cellRenderer="InActions"
   */
  cellRenderer?: string;

  /**
   * Parameters to pass to cell renderer.
   *
   * @example
   * cellRendererParams: {
   *   statusMap: {
   *     active: { text: 'Active', type: 'success' }
   *   }
   * }
   */
  cellRendererParams?: any;

  /**
   * Whether column is pinned (fixed) left or right.
   *
   * @example pinned="left"
   * @example pinned="right"
   */
  pinned?: 'left' | 'right';

  /**
   * Whether column is visible.
   *
   * @default true
   */
  hide?: boolean;

  /**
   * Whether column can be resized.
   *
   * @default true
   */
  resizable?: boolean;

  /**
   * Custom filter component for this column.
   */
  filter?: string;

  /**
   * Custom comparator function for sorting.
   */
  comparator?: (valueA: any, valueB: any) => number;
}

/**
 * InDataTableV2 component props.
 */
export interface InDataTableV2Props {
  /**
   * Unique identifier for the data table.
   *
   * @required
   * @example id="users-table"
   * @example id="orders-grid"
   */
  id: string;

  /**
   * Column definitions for the table.
   * Defines structure, headers, and cell renderers.
   *
   * @required
   * @example
   * :columns="[
   *   { key: 'name', label: 'Name', sortable: true },
   *   { key: 'email', label: 'Email', sortable: true },
   *   { key: 'role', label: 'Role', width: '120px' }
   * ]"
   */
  columns: TableColumn[];

  /**
   * Array of row data objects.
   * Each object represents one row in the table.
   *
   * @required
   * @example
   * :data="[
   *   { name: 'John Doe', email: 'john@example.com', role: 'Admin' },
   *   { name: 'Jane Smith', email: 'jane@example.com', role: 'User' }
   * ]"
   */
  data: any[];

  /**
   * Shows loading state with spinner or skeleton.
   *
   * @default false
   * @example :loading="isLoadingData"
   */
  loading?: boolean;

  /**
   * Enables pagination controls.
   *
   * @default false
   * @example :pagination="true"
   */
  pagination?: boolean;

  /**
   * Enables sorting globally.
   * Individual columns can override with sortable prop.
   *
   * @default false
   * @example :sortable="true"
   */
  sortable?: boolean;

  /**
   * Page size for pagination.
   *
   * @default 10
   * @example :page-size="25"
   */
  pageSize?: number;

  /**
   * Enables row selection (checkboxes).
   *
   * @default false
   * @example :row-selection="true"
   */
  rowSelection?: boolean;

  /**
   * Row selection mode.
   *
   * @default 'multiple'
   * @example row-selection-mode="single"
   */
  rowSelectionMode?: 'single' | 'multiple';

  /**
   * Custom grid options for AG Grid.
   * Allows full customization of AG Grid behavior.
   *
   * @example
   * :grid-options="{
   *   animateRows: true,
   *   enableCellTextSelection: true
   * }"
   */
  gridOptions?: any;
}

/**
 * InDataTableV2 Component
 *
 * Enterprise-grade data table built on AG Grid with sorting, filtering,
 * pagination, bulk actions, and custom cell renderers.
 *
 * @example
 * // Basic usage
 * <InDataTableV2
 *   id="users"
 *   :columns="[
 *     { key: 'name', label: 'Name', sortable: true },
