};`,
    tags: ['single', 'timezone', 'international', 'advanced'],
    notes: 'Timezone offset adjusts the "today" reference and all date calculations'
  },

  {
    id: 'disabled-comparison',
    title: 'Conditional Comparison Mode',
    description: 'Disable comparison based on conditions with tooltip explanation',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InDatePickerV2
  id="conditional-comparison"
  name="conditionalComparison"
  label-text="Data Period"
  :quick-range-selection-status="true"
  :comparison-status="true"
  :disabled-comparison-status="isComparisonDisabled"
  :disabled-comparison-tooltip-text="comparisonTooltip"
  @apply="handleDataPeriod"
/>`,
    props: {
      id: 'conditional-comparison',
      name: 'conditionalComparison',
      labelText: 'Data Period',
      quickRangeSelectionStatus: true,
      comparisonStatus: true
    },
    scriptSetup: `import { ref, computed } from 'vue';

const selectedDays = ref(0);

const isComparisonDisabled = computed(() => selectedDays.value > 90);

const comparisonTooltip = computed(() =>
  isComparisonDisabled.value
    ? 'Comparison is not available for date ranges longer than 90 days'
    : ''
);

const handleDataPeriod = (range: string | string[]) => {
  if (typeof range === 'string') {
    const [start, end] = range.split('-');
    const startDate = new Date(start);
    const endDate = new Date(end);
    selectedDays.value = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
  }
  console.log('Data period:', range);
};`,
    tags: ['range', 'comparison', 'conditional', 'tooltip', 'advanced']
  }
];

/**
 * Common mistakes developers make with InDatePickerV2
 */
export const antiPatterns = [
  {
    wrong: `<InDatePickerV2
  :value="selectedDate"
  @apply="selectedDate = $event"
/>`,
    right: `<InDatePickerV2
  :value="dateValue"
  @apply="handleApply"
/>

// In script
const dateValue = ref('');
const handleApply = (date) => {
  dateValue.value = date;
};`,
    reason: 'Do not directly mutate props in event handlers. Use proper Vue reactivity patterns.'
  },
  {
    wrong: `<InDatePickerV2
  :min-date="'2025-01-01'"
  :max-date="'2025-12-31'"
/>`,
    right: `<InDatePickerV2
  :min-date="'1/1/2025'"
  :max-date="'12/31/2025'"
/>`,
    reason: 'Date strings must use the format specified by the locale prop (default: mm/dd/yyyy or dd.mm.yyyy), not ISO format'
  },
  {
    wrong: `<InDatePickerV2
  :value="[startDate, endDate]"
/>`,
    right: `<InDatePickerV2
  :value="[{ startDate: '1/1/2025', endDate: '1/31/2025' }]"
/>`,
    reason: 'Value prop expects an array of DateRange objects, not an array of date strings'
  },
  {
    wrong: `<InDatePickerV2
  :custom-ranges="[
    { name: 'q1', title: 'Q1' }
  ]"
/>`,
    right: `<InDatePickerV2
  :custom-ranges="[
    { name: 'q1', title: 'Q1', startDate: '1/1/2025', endDate: '3/31/2025' }
  ]"
/>`,
    reason: 'Custom ranges must include startDate and endDate properties'
  },
  {
    wrong: `<InDatePickerV2
  :comparison-status="true"
  :single-date-picker-status="true"
/>`,
    right: `<InDatePickerV2
  :comparison-status="true"
  :single-date-picker-status="false"
/>`,
    reason: 'Comparison mode only works with date ranges, not single date picker mode'
  },
  {
    wrong: `<InDatePickerV2
  @change="handleChange"
/>`,
    right: `<InDatePickerV2
  @apply="handleApply"
/>`,
    reason: 'The primary event is "apply", not "change". Use @apply to get the selected date(s) when user confirms'
  }
];

