export function useDrawerManager(options = {}) {
  const { onOpen, onClose, resetOnClose = true } = options;

  const isOpen = ref(false);
  const data = ref(null);

  const open = (initialData) => {
    data.value = initialData || {};
    isOpen.value = true;
    onOpen?.();
  };

  const close = () => {
    isOpen.value = false;
    if (resetOnClose) {
      setTimeout(() => {
        data.value = null;
      }, 300); // Wait for close animation
    }
    onClose?.();
  };

  const toggle = () => {
    if (isOpen.value) {
      close();
    } else {
      open();
    }
  };

  return {
    isOpen,
    data,
    open,
    close,
    toggle
  };
}

