function transformApiResponse(data: any[], labelKey: string = 'name', valueKey: string = 'id'): Array<{label: string; value: any}> {
  return data.map(item => ({
    label: item[labelKey],
    value: item[valueKey]
  }));
}

function groupOptionsByKey(options: any[], groupKey: string): Array<{label: string; value: string; disabled: true; children: any[]}> {
  const groups = options.reduce((acc, option) => {
    const group = option[groupKey];
    if (!acc[group]) acc[group] = [];
    acc[group].push(option);
    return acc;
  }, {} as Record<string, any[]>);

  return Object.entries(groups).map(([groupName, items]) => ({
    label: groupName,
    value: groupName.toLowerCase(),
    disabled: true,
    children: items
  }));
}

function filterOptionsBySearch(options: Array<{label: string; value: any}>, query: string): Array<{label: string; value: any}> {
  if (!query) return options;

  const lowerQuery = query.toLowerCase();
  return options.filter(option => 
    option.label.toLowerCase().includes(lowerQuery)
  );
}

