interface APIEvent {
  event_id: string;
  event_name: string;
  display_name: string;
  event_category?: string;
  is_constant?: boolean;
}

interface APIAttribute {
  attr_id: string;
  attr_name: string;
  display_name: string;
  attr_category?: string;
  data_type?: string;
  is_constant?: boolean;
}

interface FieldsConfig {
  events: Array<{
    id: string;
    name: string;
    label: string;
    category?: string;
    isConstant?: boolean;
  }>;
  attributes: Array<{
    id: string;
    name: string;
    label: string;
    category?: string;
    type?: string;
    isConstant?: boolean;
  }>;
  partners?: Array<{
    id: string;
    name: string;
    label: string;
  }>;
}

export function createFieldsConfigFromAPI(
  apiEvents: APIEvent[],
  apiAttributes: APIAttribute[],
  apiPartners?: any[]
): FieldsConfig {
  return {
    events: apiEvents.map(event => ({
      id: event.event_id,
      name: event.event_name,
      label: event.display_name,
      category: event.event_category,
      isConstant: event.is_constant || false
    })),

    attributes: apiAttributes.map(attr => ({
      id: attr.attr_id,
      name: attr.attr_name,
      label: attr.display_name,
      category: attr.attr_category,
      type: attr.data_type,
      isConstant: attr.is_constant || false
    })),

    partners: apiPartners?.map(partner => ({
      id: partner.partner_id,
      name: partner.partner_name,
      label: partner.display_name
    })) || []
  };
}

