function createModalController(onConfirm?: () => void | Promise<void>) {
  const isOpen = ref(false);
  const isLoading = ref(false);

  const open = () => {
    isOpen.value = true;
  };

  const close = () => {
    isOpen.value = false;
  };

  const handleConfirm = async () => {
    if (!onConfirm) {
      close();
      return;
    }

    isLoading.value = true;
    try {
      await onConfirm();
      close();
    } catch (error) {
      console.error('Modal action failed:', error);
    } finally {
      isLoading.value = false;
    }
  };

  return {
    isOpen,
    isLoading,
    open,
    close,
    handleConfirm
  };
}

