function useConfirmationModal() {
  const isOpen = ref(false);
  const config = ref<{
    title: string;
    message: string;
    confirmText?: string;
    cancelText?: string;
    state?: string;
    onConfirm?: () => void;
  }>({});

  const confirm = (options: typeof config.value) => {
    return new Promise<boolean>((resolve) => {
      config.value = {
        confirmText: 'Confirm',
        cancelText: 'Cancel',
        state: 'default',
        ...options
      };

      const originalOnConfirm = config.value.onConfirm;

      config.value.onConfirm = () => {
        originalOnConfirm?.();
        resolve(true);
        isOpen.value = false;
      };

      isOpen.value = true;
    });
  };

  const handleCancel = () => {
    isOpen.value = false;
  };

  return {
    isOpen,
    config,
    confirm,
    handleCancel
  };
}

