import { ref } from 'vue';

export function useModal(options: {
  onConfirm?: () => void | Promise<void>;
  onCancel?: () => void;
  confirmBeforeClose?: boolean;
} = {}) {
  const { onConfirm, onCancel, confirmBeforeClose = false } = options;

  const isOpen = ref(false);
  const isLoading = ref(false);

  const open = () => {
    isOpen.value = true;
  };

  const close = () => {
    if (confirmBeforeClose) {
      if (confirm('Are you sure you want to close?')) {
        isOpen.value = false;
        onCancel?.();
      }
    } else {
      isOpen.value = false;
      onCancel?.();
    }
  };

  const confirm = async () => {
    if (onConfirm) {
      isLoading.value = true;
      try {
        await onConfirm();
        isOpen.value = false;
      } catch (error) {
        console.error('Confirm failed:', error);
        throw error;
      } finally {
        isLoading.value = false;
      }
    } else {
      isOpen.value = false;
    }
  };

  return {
    isOpen,
    isLoading,
    open,
    close,
    confirm
  };
}

