helperFunctions.code:
function selectAll(options: Array<{value: any}>): any[] {
  return options.filter(opt => !opt.disabled).map(opt => opt.value);
}

function deselectAll(): any[] {
  return [];
}

function toggleSelection(currentSelection: any[], value: any): any[] {
  const index = currentSelection.indexOf(value);
  if (index > -1) {
    return currentSelection.filter(v => v !== value);
  }
  return [...currentSelection, value];
}

function getSelectedLabels(options: Array<{label: string; value: any}>, selectedValues: any[]): string[] {
  return options
    .filter(opt => selectedValues.includes(opt.value))
    .map(opt => opt.label);
}

