export const antiPatterns = [
  {
    wrong: `<InMultiSelect :options="options" />`,
    right: `<InMultiSelect id="my-select" :options="options" :value="selected" />`,
    reason: 'id prop is required, and value should be controlled'
  },
  {
    wrong: `const options = [{ text: 'Item' }];`,
    right: `const options = [{ text: 'Item', value: 'item' }];`,
    reason: 'Both text and value are required for options'
  },
  {
    wrong: `@select="selected = $event"`,
    right: `@select="handleSelect"`,
    reason: 'Use a proper handler function for better control'
