function transformToOptions(items: string[]) {
  return items.map(item => ({
    text: item,
    value: item.toLowerCase().replace(/\s+/g, '-')
  }));
}

function getSelectedValues(selected: Array<{ text: string, value: any }>) {
  return selected.map(item => item.value);
}

