export function createEmptyState(type, options = {}) {
  const configs = {
    search: {
      title: `No results for "${options.query}"`,
      description: 'Try different keywords or clear filters',
      icon: 'search',
      buttonStatus: true,
      buttonText: 'Clear Search'
    },
    filtered: {
      title: 'No matches found',
      description: 'No items match your current filters. Try adjusting your criteria.',
      icon: 'filter',
      buttonStatus: true,
      buttonText: 'Clear Filters'
    },
    empty: {
      title: options.title || 'No items yet',
      description: options.description || 'Create your first item to get started',
      icon: options.icon || 'inbox-empty',
      buttonStatus: options.canCreate ?? true,
      buttonText: options.createText || 'Create Item'
    },
    permission: {
      title: 'Access Restricted',
      description: 'You don\'t have permission to view this content',
      icon: 'lock',
      buttonStatus: false
    },
    error: {
      title: 'Unable to load data',
      description: options.error || 'An error occurred while loading. Please try again.',
      icon: 'alert-triangle',
      buttonStatus: true,
      buttonText: 'Retry'
    }
  };

  return { ...configs[type], ...options };
}

