function showTemporaryRibbon(ribbonRef: Ref<boolean>, duration = 5000) {
  ribbonRef.value = true;

  return setTimeout(() => {
    ribbonRef.value = false;
  }, duration);
}

function createNotificationQueue() {
  const queue: Array<{ text: string; type: string }> = [];
  const current = ref<{ text: string; type: string } | null>(null);
  const isVisible = ref(false);

  const add = (text: string, type: string) => {
    queue.push({ text, type });
    if (!isVisible.value) showNext();
  };

  const showNext = () => {
    if (queue.length === 0) {
      isVisible.value = false;
      current.value = null;
      return;
    }

    current.value = queue.shift()!;
    isVisible.value = true;

    setTimeout(() => {
      showNext();
    }, 4000);
  };

  const clear = () => {
    queue.length = 0;
    isVisible.value = false;
    current.value = null;
  };

  return { current, isVisible, add, clear };
}

