 */
export const antiPatterns = [
  {
    wrong: `<InRichTextInput v-model="content" />`,
    right: `<InRichTextInput id="editor" v-model="content" />`,
    reason: 'id prop is required for proper editor initialization'
  },
  {
    wrong: `<InRichTextInput id="editor" :value="content" />`,
    right: `<InRichTextInput id="editor" v-model="content" />`,
    reason: 'Use v-model for two-way binding with rich text editor'
  },
  {
    wrong: `const content = ref(null);`,
    right: `const content = ref('');`,
    reason: 'Initialize content with empty string, not null'
  },
  {
    wrong: `:toolbar="'bold,italic,underline'"`,
    right: `:toolbar="['bold', 'italic', 'underline']"`,
    reason: 'toolbar prop expects an array, not a string'
  }
];

/**
 * Best practices for using InRichTextInput
 */
export const bestPractices = [
  {
    practice: 'Always use v-model',
