helperFunctions.code:
function stripHtmlTags(html: string): string {
  return html.replace(/<[^>]*>/g, '');
}

function calculateMergeTagLength(content: string, tagLengths: Record<string, number>): number {
  let text = content.replace(/<[^>]*>/g, '');

  Object.entries(tagLengths).forEach(([tag, length]) => {
    const regex = new RegExp(`{{${tag}}}`, 'g');
    text = text.replace(regex, 'X'.repeat(length));
  });

  return text.length;
}

helperFunctions.example:
const length = calculateMergeTagLength(
  'Hello {{name}}',
  { name: 50 }
);
// Returns: 56 (Hello  + 50 chars)

helperFunctions.code:
function sanitizeRichText(html: string, allowedTags: string[] = ['p', 'b', 'i', 'u', 'a', 'br']): string {
  const div = document.createElement('div');
  div.innerHTML = html;

  const walk = (node: Node) => {
    if (node.nodeType === 1) { // Element node
      const tagName = (node as Element).tagName.toLowerCase();
      if (!allowedTags.includes(tagName)) {
        // Replace with text content
        const textNode = document.createTextNode(node.textContent || '');
        node.parentNode?.replaceChild(textNode, node);
      }
    }

    node.childNodes.forEach(walk);
  };

  walk(div);
  return div.innerHTML;
}

