    text: 'Clothing',
    value: 'clothing',
    children: [
      { text: 'Men', value: 'men' },
      { text: 'Women', value: 'women' },
      { text: 'Kids', value: 'kids' }
    ]
  }
]);

const selectedCategory = ref([]);

const handleSelect = (option) => {
  selectedCategory.value = [option];
  console.log('Selected category:', option);
};`,
    tags: ['nested', 'hierarchical', 'common']
  },

  {
    id: 'lazy-loading',
    title: 'Lazy Loading',
    description: 'Load options on scroll for large datasets',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InSelect
  id="product-select"
  label-text="Select Product"
  :options="products"
  :value="selectedProduct"
  :lazy-load="true"
  :loading-state="isLoading"
  :fetch-page="currentPage"
  @endScroll="loadMore"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref } from 'vue';

const products = ref([]);
const selectedProduct = ref([]);
const currentPage = ref(1);
const isLoading = ref(false);

const loadMore = async (page) => {
  isLoading.value = true;
  try {
    const response = await fetch(\`/api/products?page=\${page}\`);
    const data = await response.json();
    products.value = [...products.value, ...data.products];
    currentPage.value = page;
  } finally {
    isLoading.value = false;
  }
};

const handleSelect = (option) => {
  selectedProduct.value = [option];
};

// Load initial data
loadMore(1);`,
    tags: ['lazy-load', 'pagination', 'advanced', 'performance']
  },

  {
    id: 'with-validation',
    title: 'Select with Validation',
    description: 'Form validation with error and warning states',
    category: 'common',
    complexity: 'intermediate',
    code: `<InSelect
  id="status-select"
  label-text="Status"
  :options="statusOptions"
  :value="selectedStatus"
  :state="validationState"
  :state-message="validationMessage"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref, computed } from 'vue';

const statusOptions = ref([
  { text: 'Active', value: 'active' },
  { text: 'Inactive', value: 'inactive' },
  { text: 'Pending', value: 'pending' }
]);

const selectedStatus = ref([]);

const validationState = computed(() => {
  if (selectedStatus.value.length === 0) return 'error';
  return 'default';
});

const validationMessage = computed(() => {
  if (selectedStatus.value.length === 0) return 'Please select a status';
  return '';
});

const handleSelect = (option) => {
  selectedStatus.value = [option];
};`,
    tags: ['validation', 'error', 'form', 'common']
  },

  {
    id: 'clear-selection',
    title: 'Clearable Select',
    description: 'Select with clear button to reset selection',
    category: 'common',
    complexity: 'beginner',
    code: `<InSelect
  id="filter-select"
  label-text="Filter By"
  :options="filterOptions"
  :value="selectedFilter"
  :clear-selection-button="true"
  placeholder-text="Select a filter"
  @select="handleSelect"
  @unSelect="handleClear"
/>`,
    scriptSetup: `import { ref } from 'vue';

const filterOptions = ref([
  { text: 'Last 7 days', value: '7d' },
  { text: 'Last 30 days', value: '30d' },
  { text: 'Last 90 days', value: '90d' }
]);

const selectedFilter = ref([]);

const handleSelect = (option) => {
  selectedFilter.value = [option];
  console.log('Filter applied:', option);
};

const handleClear = () => {
  selectedFilter.value = [];
  console.log('Filter cleared');
};`,
    tags: ['clear', 'reset', 'common']
  },

  {
    id: 'create-option',
    title: 'Create Custom Option',
    description: 'Allow users to create new options',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InSelect
  id="tag-select"
  label-text="Tags"
  :options="tags"
  :value="selectedTag"
  :create-option-status="true"
  create-button-label="Create Tag"
  :option-validation-type="'text'"
  :min-character-limit="2"
  :max-character-limit="20"
  @addOption="handleAddOption"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref } from 'vue';

const tags = ref([
  { text: 'Bug', value: 'bug' },
  { text: 'Feature', value: 'feature' },
  { text: 'Enhancement', value: 'enhancement' }
]);

const selectedTag = ref([]);

const handleAddOption = (option) => {
  // Add new tag to options
  tags.value.push(option);
  // Select the newly created tag
  selectedTag.value = [option];
  console.log('New tag created:', option);
};

const handleSelect = (option) => {
  selectedTag.value = [option];
};`,
    tags: ['create', 'custom-option', 'advanced']
  },

  {
    id: 'search-lazy-load',
    title: 'Search with Lazy Loading',
    description: 'Combine search and lazy loading for API-driven options',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InSelect
  id="api-select"
  label-text="Search Products"
  :options="searchResults"
  :value="selectedProduct"
  :search-status="true"
  :lazy-load-search="true"
  :loading-state="isSearching"
  placeholder-text="Type to search..."
  @search="handleSearch"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref } from 'vue';

const searchResults = ref([]);
const selectedProduct = ref([]);
const isSearching = ref(false);

let searchTimeout;

const handleSearch = async (query) => {
  // Debounce search
  clearTimeout(searchTimeout);

  if (!query) {
    searchResults.value = [];
    return;
  }

  searchTimeout = setTimeout(async () => {
    isSearching.value = true;
    try {
      const response = await fetch(\`/api/products/search?q=\${query}\`);
      const data = await response.json();
      searchResults.value = data.products.map(p => ({
        text: p.name,
        value: p.id
      }));
    } finally {
      isSearching.value = false;
    }
  }, 300);
};

const handleSelect = (option) => {
  selectedProduct.value = [option];
};`,
    tags: ['search', 'lazy-load', 'api', 'advanced', 'debounce']
  },

  {
    id: 'dynamic-position',
    title: 'Dynamic Positioning',
    description: 'Auto-adjust dropdown position based on viewport',
    category: 'advanced',
    complexity: 'intermediate',
