  id="smart-select"
  label-text="Smart Positioning"
  :options="options"
  :value="selectedOption"
  :dynamic-position="true"
  :bottom-offset-for-dynamic-position="50"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref } from 'vue';

const options = ref([
  { text: 'Option 1', value: '1' },
  { text: 'Option 2', value: '2' },
  { text: 'Option 3', value: '3' }
]);

const selectedOption = ref([]);

const handleSelect = (option) => {
  selectedOption.value = [option];
};`,
    tags: ['positioning', 'dynamic', 'viewport', 'advanced'],
    notes: 'Useful for selects near bottom of viewport or in modals'
  },

  {
    id: 'with-icons-descriptions',
    title: 'Options with Icons and Descriptions',
    description: 'Rich options with icons and descriptions',
    category: 'advanced',
    complexity: 'intermediate',
    code: `<InSelect
  id="rich-select"
  label-text="Choose Action"
  :options="actions"
  :value="selectedAction"
  :is-wrapped-description="true"
  @select="handleSelect"
/>`,
    scriptSetup: `import { ref } from 'vue';

const actions = ref([
  {
    text: 'Save',
    value: 'save',
    icon: 'filled-save',
    description: 'Save changes to the database'
  },
  {
    text: 'Export',
    value: 'export',
    icon: 'filled-export',
    description: 'Export data to CSV or Excel format'
  },
  {
    text: 'Delete',
    value: 'delete',
    icon: 'filled-trash',
    description: 'Permanently delete the selected items'
  }
]);

const selectedAction = ref([]);

const handleSelect = (option) => {
  selectedAction.value = [option];
  console.log('Action selected:', option);
};`,
    tags: ['icons', 'descriptions', 'rich-options', 'advanced']
  }
];

export const antiPatterns = [
  {
    wrong: `<InSelect :options="users" />`,
    right: `<InSelect
  :options="users"
  :value="selectedUsers"
  @select="handleSelect"
/>`,
    reason: 'Always provide value and select handler for controlled component behavior'
  },
  {
    wrong: `<InSelect
  :options="options"
  :value="selectedValue"
/>

// selectedValue is a string`,
    right: `<InSelect
  :options="options"
  :value="[{ text: 'Option', value: '1' }]"
/>

// Value must be an array of option objects`,
    reason: 'Value prop expects array of SelectOption objects, not strings'
  },
  {
    wrong: `@select="selectedOption = $event"`,
    right: `@select="handleSelect"

const handleSelect = (option) => {
  selectedOption.value = [option];
};`,
    reason: 'Use proper handler function instead of direct assignment in template'
  }
];

InSelect.typeDefs:
import { DefineComponent } from 'vue';

/**
 * Theme variants for the select component.
 */
export type SelectTheme = 'grey' | 'white';

/**
 * State variants for validation and feedback.
 */
export type SelectState = 'default' | 'error' | 'warning';

/**
 * Static position options for dropdown menu.
 */
export type StaticPosition = 'bottom left' | 'bottom right' | 'top left' | 'top right';

/**
 * Option validation types.
 */
export type OptionValidationType = 'all' | 'number' | 'text' | RegExp;

/**
 * Select option structure.
 */
export interface SelectOption {
  /**
   * Display text for the option.
   */
  text: string;

  /**
   * Value for the option.
   */
  value: string | number;

  /**
   * Optional child options for nested select.
   */
  children?: SelectOption[];

  /**
   * Mark option as inactive (not selectable but clickable).
   * @default false
   */
  inActive?: boolean;

  /**
   * Optional description text.
   */
  description?: string;

  /**
   * Optional icon name.
   */
  icon?: string;
}

/**
 * Skeleton loader sizing configuration.
 */
export interface SkeletonSizing {
  /**
   * Skeleton width in pixels.
   */
  width: number;

  /**
   * Number of skeleton elements.
   */
  number: number;
}

/**
 * InSelect component props.
 */
export interface InSelectProps {
  /**
   * Unique identifier for the select element.
   * Used for DOM manipulation, testing, and accessibility.
   *
   * @default 'qa-select'
   * @example 'country-select'
   * @example 'user-status-select'
   */
  id?: string;

  /**
   * Array of options to display in the dropdown.
   * Supports nested options with children property.
   *
   * @default []
   * @example
   * [
   *   { text: 'Option 1', value: '1' },
   *   { text: 'Option 2', value: '2' }
   * ]
   * @example
   * // With nested options
   * [
   *   {
   *     text: 'Group 1',
   *     value: 'group1',
   *     children: [
   *       { text: 'Option 1.1', value: '1.1' },
   *       { text: 'Option 1.2', value: '1.2' }
   *     ]
   *   }
   * ]
   */
  options?: SelectOption[];

  /**
   * Currently selected value(s).
   * Array with single selected option object.
   *
   * @default []
   * @example [{ text: 'Selected Option', value: '1' }]
   */
  value?: SelectOption[];

  /**
   * Shows loading state in dropdown menu.
   * Displays spinner while options are being fetched.
   *
   * @default false
   * @example :loading-state="isLoadingOptions"
   */
  loadingState?: boolean;

  /**
   * Shows the label above the select.
   *
   * @default true
   * @example :label-status="false" // Hide label
   */
  labelStatus?: boolean;

  /**
   * Label text content.
   *
   * @default ''
   * @example 'Select Country'
   * @example 'Choose Status'
   */
  labelText?: string;

  /**
   * Shows tooltip icon next to label.
   *
   * @default false
   * @example :tooltip-status="true"
   */
  tooltipStatus?: boolean;

  /**
   * Tooltip text content.
   *
   * @default ''
   * @example 'Select your preferred country from the list'
   */
  tooltipText?: string;

  /**
   * Disables the select component.
   * Prevents all user interaction.
   *
   * @default false
   * @example :disabled-status="!hasPermission"
   */
  disabledStatus?: boolean;

  /**
   * Controls dropdown menu visibility (controlled mode).
   * When provided, component operates in controlled mode.
   *
   * @default false
   * @example :dropdown-menu-status="isMenuOpen"
   */
  dropdownMenuStatus?: boolean;

  /**
   * Enables search functionality in dropdown menu.
   * Allows filtering options by typing.
   *
   * @default false
   * @example :search-status="true"
   */
  searchStatus?: boolean;

  /**
   * Visual theme variant of the select.
   *
   * @default 'grey'
   * @example 'grey' // Default grey background
   * @example 'white' // White background variant
   */
  theme?: SelectTheme;

  /**
   * Button type for select display.
   *
   * @default 'dropdown'
   * @example 'dropdown'
   */
  buttonType?: string;

  /**
   * Placeholder text when no option is selected.
   *
   * @default ''
   * @example 'Select an option...'
   * @example 'Choose country'
   */
  placeholderText?: string;

  /**
   * Shows clear selection button (X icon) when option is selected.
   * Allows clearing the selection.
   *
   * @default false
   * @example :clear-selection-button="true"
   */
  clearSelectionButton?: boolean;

  /**
   * Static position for dropdown menu when dynamicPosition is false.
   *
   * @default 'bottom left'
   * @example 'bottom left'
   * @example 'bottom right'
   * @example 'top left'
   * @example 'top right'
   */
  staticPosition?: StaticPosition;

  /**
   * Enables dynamic positioning of dropdown menu.
   * Automatically adjusts position based on viewport space.
   *
   * @default false
   * @example :dynamic-position="true"
   */
  dynamicPosition?: boolean;

  /**
   * Enables dynamic positioning for child (nested) menus.
   *
   * @default true
   * @example :dynamic-child-position="false"
   */
  dynamicChildPosition?: boolean;

  /**
   * Static position for child (nested) dropdown menus.
   *
   * @default 'bottom right'
   * @example 'bottom right'
   * @example 'top right'
   */
  staticChildPosition?: StaticPosition;

  /**
   * Label text for create new option button.
   * Only shown when createOptionStatus is true.
   *
   * @default ''
   * @example 'Create New Option'
   */
  createButtonLabel?: string;

  /**
   * Enables create new option functionality.
   * Allows users to add custom options.
   *
   * @default false
   * @example :create-option-status="true"
   */
  createOptionStatus?: boolean;

  /**
   * Shows secondary button at bottom of dropdown.
   *
   * @default true
   * @example :button-status="false"
   */
  buttonStatus?: boolean;

  /**
   * Icon name for secondary button.
   *
   * @default ''
   * @example 'filled-plus'
   */
  buttonIcon?: string;

  /**
   * Label text for secondary button.
   *
   * @default ''
   * @example 'Add New Item'
   */
  buttonLabel?: string;

  /**
   * Validation state of the select.
   * Affects visual appearance and shows state message.
   *
   * @default 'default'
   * @example 'default' // Normal state
   * @example 'error' // Shows error styling and message
   * @example 'warning' // Shows warning styling and message
   */
  state?: SelectState;

  /**
   * State message displayed for error or warning states.
   *
   * @default 'State Message'
   * @example 'Please select a value'
   * @example 'This field is required'
   */
  stateMessage?: string;

  /**
   * Shows skeleton loading placeholder.
   * Used during initial page load or data fetching.
   *
   * @default false
   * @example :skeleton-status="isLoading"
   */
  skeletonStatus?: boolean;

  /**
   * Dimensions for the skeleton loader.
   *
   * @default { width: 0, number: 0 }
   * @example :skeleton-sizing="{ width: 300, number: 1 }"
   */
  skeletonSizing?: SkeletonSizing;

  /**
   * Validation type for create option input.
   * Can be predefined type or custom RegExp.
   *
   * @default 'all'
   * @example 'all' // Allow any character
   * @example 'number' // Only numbers
   * @example 'text' // Only text
   * @example /^[A-Z]+$/ // Custom regex
   */
  optionValidationType?: OptionValidationType;

  /**
   * Minimum character limit for create option input.
   *
   * @default 1
   * @example :min-character-limit="3"
   */
  minCharacterLimit?: number | string;

  /**
   * Maximum character limit for create option input.
   *
   * @default 10
   * @example :max-character-limit="50"
   */
  maxCharacterLimit?: number | string;

  /**
   * Validation message for invalid option input.
   *
   * @default 'Enter a valid value to continue.'
   * @example 'Please enter a valid option name'
   */
  optionValidationText?: string;

  /**
   * Validation message for character limit violations.
   *
   * @default 'Enter a value between 1 and 10 characters to continue.'
   * @example 'Option name must be between 3 and 50 characters'
   */
  characterLimitValidationText?: string;

  /**
   * Bottom offset for dynamic position calculation.
   * Adjusts dropdown position from bottom of viewport.
   *
   * @default 0
   * @example :bottom-offset-for-dynamic-position="100"
   */
  bottomOffsetForDynamicPosition?: number;

  /**
   * Maximum height of dropdown menu in pixels.
   * Menu becomes scrollable when exceeded.
   *
   * @default 240
   * @example :max-height="300"
   */
  maxHeight?: number;

  /**
   * Maximum height of child (nested) dropdown menus.
   *
   * @default 480
   * @example :max-child-height="400"
   */
  maxChildHeight?: number;

  /**
   * Current page number for lazy loading.
   * Used with lazyLoad to track pagination.
   *
   * @default 1
   * @example :fetch-page="currentPage"
   */
  fetchPage?: number;

  /**
   * Enables lazy loading for options.
   * Loads more options on scroll to end.
   *
   * @default false
   * @example :lazy-load="true"
   */
  lazyLoad?: boolean;

  /**
   * Enables lazy loading for search results.
   * Triggers search event on typing.
   *
   * @default false
   * @example :lazy-load-search="true"
   */
  lazyLoadSearch?: boolean;

  /**
   * Enables text wrapping for option descriptions.
   * When false, descriptions are truncated with ellipsis.
   *
   * @default false
   * @example :is-wrapped-description="true"
   */
  isWrappedDescription?: boolean;

  /**
   * Shows helper message below the select.
   *
   * @default false
   * @example :helper-message-status="true"
   */
  helperMessageStatus?: boolean;

  /**
   * Helper message text content.
   * Provides additional guidance to users.
   *
   * @default ''
   * @example 'Select the country where you are located'
   */
  helperMessage?: string;

  /**
   * Custom text to display instead of selected option text.
   * Overrides the selected option's text display.
   *
   * @default ''
   * @example 'Custom display text'
   */
  customOptionText?: string;

  /**
   * Enables XSS protection for label text.
   * Sanitizes label text to prevent XSS attacks.
   *
   * @default true
   * @example :prevent-xss="false"
   */
  preventXss?: boolean;

  /**
   * Prevents auto-focus on dropdown menu open.
   * Useful when managing focus externally.
   *
   * @default false
   * @example :prevent-focus="true"
   */
  preventFocus?: boolean;

  /**
   * Allows adding options in any case (uppercase/lowercase).
   * When false, enforces specific casing rules.
   *
   * @default true
   * @example :allow-add-all-cases="false"
   */
  allowAddAllCases?: boolean;

  /**
   * Validation message when trying to add duplicate value.
   *
   * @default 'This value already exists.'
   * @example 'This option is already in the list'
   */
  sameValueExistsValidationText?: string;
}

/**
 * Event handler types for InSelect.
 */
export interface InSelectEventHandlers {
  /**
   * Emitted when an option is selected.
   *
   * @param option - Selected option object
   * @example
   * const handleSelect = (option: SelectOption) => {
   *   console.log('Selected:', option.text, option.value);
   * };
   */
  onSelect?: (option: SelectOption | string) => void;

  /**
   * Emitted when selection is cleared (only with clearSelectionButton).
   *
   * @param option - Previously selected option
   */
  onUnSelect?: (option: SelectOption[]) => void;

  /**
   * Emitted when an inactive option is clicked.
   *
   * @param option - Clicked inactive option
   */
  onSelectedInActiveOption?: (option: SelectOption) => void;

  /**
   * Emitted when user types in search input.
   *
   * @param searchString - Current search query
   * @example
   * const handleSearch = (query: string) => {
   *   // Filter options or fetch from API
   * };
   */
  onSearch?: (searchString: string) => void;

  /**
   * Emitted when user scrolls to end of dropdown (lazy load).
   *
   * @param page - Next page number to fetch
   * @example
   * const handleEndScroll = (page: number) => {
   *   fetchMoreOptions(page);
   * };
   */
  onEndScroll?: (page: number) => void;

  /**
   * Emitted when create new option is added.
   *
   * @param option - Newly created option object
   */
  onAddOption?: (option: SelectOption) => void;

  /**
   * Emitted when dropdown menu opens or closes.
   *
   * @param isOpen - Whether dropdown is now open
   */
  onOpenList?: (isOpen: boolean) => void;

  /**
   * Emitted when dropdown menu closes.
   */
  onClose?: () => void;

  /**
   * Emitted when secondary button is clicked.
   */
  onSecondaryButtonClick?: () => void;

  /**
   * Emitted when info box button is clicked.
   *
   * @param payload - Info box button click payload
   */
  onInfoBoxButtonClick?: (payload: any) => void;

  /**
   * Emitted when info box icon is clicked.
   *
   * @param payload - Info box icon click payload
   */
  onInfoBoxClickIcon?: (payload: any) => void;
}

/**
 * InSelect emits configuration (for Vue's EmitsOptions).
 */
export type InSelectEmits = {
  select: (option: SelectOption | string) => true;
  unSelect: (option: SelectOption[]) => true;
  selectedInActiveOption: (option: SelectOption) => true;
  search: (searchString: string) => true;
  endScroll: (page: number) => true;
  addOption: (option: SelectOption) => true;
  openList: (isOpen: boolean) => true;
  close: () => true;
  secondaryButtonClick: () => true;
  infoBoxButtonClick: (payload: any) => true;
  infoBoxClickIcon: (payload: any) => true;
};

/**
 * InSelect Component
 *
 * Dropdown select component with search, lazy loading, nested options, and custom option creation.
 * Supports single selection with extensive customization options.
 *
 * @example
 * // Basic usage
 * <InSelect
 *   id="country-select"
 *   label-text="Country"
 *   :options="countries"
 *   :value="selectedCountry"
 *   placeholder-text="Select a country"
 *   @select="handleSelect"
 * />
 *
 * @example
 * // With search and lazy loading
 * <InSelect
 *   id="user-select"
 *   label-text="Select User"
 *   :options="users"
 *   :value="selectedUser"
 *   :search-status="true"
 *   :lazy-load="true"
 *   :lazy-load-search="true"
 *   :loading-state="isLoading"
 *   @search="handleSearch"
 *   @endScroll="loadMoreUsers"
 *   @select="handleSelect"
 * />
 *
 * @example
 * // With nested options
 * <InSelect
 *   id="category-select"
 *   label-text="Category"
 *   :options="[
 *     {
 *       text: 'Electronics',
 *       value: 'electronics',
 *       children: [
 *         { text: 'Phones', value: 'phones' },
 *         { text: 'Laptops', value: 'laptops' }
 *       ]
 *     }
 *   ]"
 *   @select="handleSelect"
 * />
 */
declare const InSelect: DefineComponent<InSelectProps, {}, {}, {}, {}, {}, {}, InSelectEmits>;

export default InSelect;

commonMistakes.example:
// Wrong:
{ label: 'Option 1', value: 1 }

// Correct:
{ text: 'Option 1', value: 1 }

// Wrong:
value="selected"

// Correct:
value="[selected]"

// Wrong:
const val = selected[0].value;

// Correct:
const val = selected[0]?.value || null;

optionClick.handlingPattern:
@optionClick="handleSelect"

handleSelect(option) {
