helperFunctions.code:
function createRoute(id: string, label: string, path: string, options = {}) {
  return {
    id,
    label,
    path,
    icon: null,
    badge: null,
    active: false,
    children: null,
    ...options
  };
}

function findActiveRoute(routes: any[], currentPath: string): any | null {
  for (const route of routes) {
    if (route.path === currentPath) return route;
    if (route.children) {
      const found = findActiveRoute(route.children, currentPath);
      if (found) return found;
    }
  }
  return null;
}

function updateRouteBadges(routes: any[], badgeMap: Record<string, number>) {
  return routes.map(route => ({
    ...route,
    badge: badgeMap[route.id] || route.badge,
    children: route.children ? updateRouteBadges(route.children, badgeMap) : route.children
  }));
}

