import { useRoute } from 'vue-router';

const route = useRoute();
const routes = computed(() => [
  { 
    id: 'home', 
    label: 'Home', 
    path: '/',
    active: route.path === '/'
  }
]);

const loadChildren = async (parentId) => {
  const children = await api.getChildRoutes(parentId);
  const parent = routes.value.find(r => r.id === parentId);
  if (parent) parent.children = children;
};

const handleExpand = (route) => {
  if (!route.children) loadChildren(route.id);
};

const saveSearch = (query) => {
  const recent = JSON.parse(localStorage.getItem('searches') || '[]');
  const updated = [query, ...recent.filter(s => s !== query)].slice(0, 10);
  localStorage.setItem('searches', JSON.stringify(updated));
  recentSearches.value = updated;
};

const handleSearchResultClick = (result) => {
  saveSearch(result.query);
  router.push(result.path);
};

