export function createSkeletonConfig(type: string, options = {}) {
  const configs = {
    avatar: {
      width: options.size || '40px',
      height: options.size || '40px',
      borderRadius: '50%'
    },
    textLine: {
      width: options.width || '100%',
      height: options.height || '16px',
      borderRadius: '4px'
    },
    heading: {
      width: options.width || '60%',
      height: '24px',
      borderRadius: '4px'
    },
    card: {
      width: options.width || '300px',
      height: options.height || '200px',
      borderRadius: '8px'
    },
    button: {
      width: options.width || '120px',
      height: '36px',
      borderRadius: '4px'
    },
    thumbnail: {
      width: options.width || '80px',
      height: options.height || '80px',
      borderRadius: '8px'
    }
  };

  return configs[type] || configs.textLine;
}

