function calculateAttributeLength(
  content: string,
  attributeMaxLengths: Record<string, number>
): number {
  let estimatedLength = content.length;

  Object.entries(attributeMaxLengths).forEach(([attr, maxLen]) => {
    const tagPattern = new RegExp(`{{${attr}}}`, 'g');
    const matches = content.match(tagPattern) || [];
    const tagLength = `{{${attr}}}`.length;

    // Replace tag length with actual max length
    estimatedLength += matches.length * (maxLen - tagLength);
  });

  return estimatedLength;
}

