function extractAttributes(content: string): string[] {
  const attributePattern = /{{([^}]+)}}/g;
  const matches = content.matchAll(attributePattern);

  return Array.from(matches, match => match[1]);
}

function replaceAttributes(
  template: string,
  attributeValues: Record<string, string>,
  fallbackText: string = ''
): string {
  return template.replace(/{{([^}]+)}}/g, (match, attr) => {
    return attributeValues[attr] || fallbackText || match;
  });
}

