function validateAttributeSyntax(content: string): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  // Check for unclosed tags
  const openBraces = (content.match(/{{/g) || []).length;
  const closeBraces = (content.match(/}}/g) || []).length;

  if (openBraces !== closeBraces) {
    errors.push('Mismatched braces: unclosed attribute tag');
  }

  // Check for empty tags
  if (/{{\s*}}/.test(content)) {
    errors.push('Empty attribute tag found');
  }

  return {
    valid: errors.length === 0,
    errors
  };
}

