export function useTabManager(initialTabs = []) {
  const tabs = ref([...initialTabs]);
  let nextId = Math.max(...tabs.value.map(t => t.id), 0) + 1;

  const addTab = (tab) => {
    tabs.value.push({
      ...tab,
      id: tab.id || nextId++
    });
  };

  const removeTab = (id) => {
    const index = tabs.value.findIndex(t => t.id === id);
    if (index !== -1) tabs.value.splice(index, 1);
  };

  const reorderTabs = (newOrder) => {
    tabs.value = [...newOrder];
  };

  const updateTab = (id, updates) => {
    const tab = tabs.value.find(t => t.id === id);
    if (tab) Object.assign(tab, updates);
  };

  return {
    tabs,
    addTab,
    removeTab,
    reorderTabs,
    updateTab
  };
}

