export function persistTabs(storageKey, defaultTabs = []) {
  const tabs = ref(defaultTabs);

  // Load from storage
  const loadTabs = () => {
    try {
      const saved = localStorage.getItem(storageKey);
      if (saved) {
        tabs.value = JSON.parse(saved);
      }
    } catch (e) {
      console.error('Failed to load tabs:', e);
    }
  };

  // Save to storage
  const saveTabs = () => {
    try {
      localStorage.setItem(storageKey, JSON.stringify(tabs.value));
    } catch (e) {
      console.error('Failed to save tabs:', e);
    }
  };

  // Reset to defaults
  const resetTabs = () => {
    tabs.value = [...defaultTabs];
    saveTabs();
  };

  // Auto-load on mount
  onMounted(loadTabs);

  // Auto-save on change
  watch(tabs, saveTabs, { deep: true });

  return { tabs, loadTabs, saveTabs, resetTabs };
}

