import { ref, computed } from 'vue';

export function useFormField<T = string>(
  initialValue: T,
  validator?: (value: T) => string
) {
  const value = ref<T>(initialValue);
  const error = ref('');
  const touched = ref(false);
  const dirty = ref(false);

  const isValid = computed(() => error.value === '');
  const showError = computed(() => touched.value && error.value !== '');

  const validate = () => {
    if (validator) {
      error.value = validator(value.value);
    } else {
      error.value = '';
    }
    return isValid.value;
  };

  const handleBlur = () => {
    touched.value = true;
    validate();
  };

  const handleInput = () => {
    dirty.value = true;
    if (touched.value) {
      // Clear errors while typing after initial validation
      error.value = '';
    }
  };

  const reset = () => {
    value.value = initialValue;
    error.value = '';
    touched.value = false;
    dirty.value = false;
  };

  return {
    value,
    error,
    touched,
    dirty,
    isValid,
    showError,
    validate,
    handleBlur,
    handleInput,
    reset
  };
}

