helperFunctions.code:
interface PasswordRequirements {
  minLength?: number;
  requireUppercase?: boolean;
  requireLowercase?: boolean;
  requireNumbers?: boolean;
  requireSpecialChars?: boolean;
  specialChars?: string;
}

export function createPasswordValidator(
  requirements: PasswordRequirements = {}
) {
  const {
    minLength = 8,
    requireUppercase = true,
    requireLowercase = true,
    requireNumbers = true,
    requireSpecialChars = false,
    specialChars = '!@#$%^&*()_+-=[]{}|;:,.<>?'
  } = requirements;

  return (password: string): string => {
    if (!password) {
      return 'Password is required';
    }

    if (password.length < minLength) {
      return `Password must be at least ${minLength} characters`;
    }

    if (requireUppercase && !/[A-Z]/.test(password)) {
      return 'Password must contain at least one uppercase letter';
    }

    if (requireLowercase && !/[a-z]/.test(password)) {
      return 'Password must contain at least one lowercase letter';
    }

    if (requireNumbers && !/\d/.test(password)) {
      return 'Password must contain at least one number';
    }

    if (requireSpecialChars) {
      const escapedChars = specialChars.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
      const regex = new RegExp(`[${escapedChars}]`);
      if (!regex.test(password)) {
        return `Password must contain at least one special character (${specialChars})`;
      }
    }

    return ''; // Valid
  };
}

