import { ref, computed } from 'vue';
import { useDebounceFn } from '@vueuse/core';

export function useSearchInput<T = any>(
  searchFn: (query: string) => Promise<T[]>,
  options: {
    debounceMs?: number;
    minLength?: number;
  } = {}
) {
  const { debounceMs = 300, minLength = 3 } = options;

  const query = ref('');
  const results = ref<T[]>([]);
  const loading = ref(false);
  const error = ref<string | null>(null);

  const hasQuery = computed(() => query.value.length >= minLength);
  const hasResults = computed(() => results.value.length > 0);

  const performSearch = async () => {
    if (query.value.length < minLength) {
      results.value = [];
      return;
    }

    loading.value = true;
    error.value = null;

    try {
      results.value = await searchFn(query.value);
    } catch (err: any) {
      error.value = err.message || 'Search failed';
      results.value = [];
    } finally {
      loading.value = false;
    }
  };

  const debouncedSearch = useDebounceFn(performSearch, debounceMs);

  const handleInput = () => {
    if (query.value.length === 0) {
      results.value = [];
      error.value = null;
    } else if (query.value.length >= minLength) {
      debouncedSearch();
    }
  };

  const clear = () => {
    query.value = '';
    results.value = [];
    error.value = null;
  };

  const executeImmediate = () => {
    if (hasQuery.value) {
      performSearch();
    }
  };

  return {
    query,
    results,
    loading,
    error,
    hasQuery,
    hasResults,
    handleInput,
    executeImmediate,
    clear
  };
}

