export function useToastManager() {
  const toasts = ref([]);
  const maxToasts = 5;
  let toastId = 0;

  const show = (options) => {
    const id = toastId++;
    const duration = options.duration || (options.type === 'error' ? 5000 : 3000);

    const toast = {
      id,
      status: true,
      ...options
    };

    // Remove oldest if at max
    if (toasts.value.length >= maxToasts) {
      toasts.value.shift();
    }

    toasts.value.push(toast);

    // Auto-dismiss
    const timeoutId = setTimeout(() => {
      remove(id);
    }, duration);

    // Return controls
    return {
      id,
      dismiss: () => {
        clearTimeout(timeoutId);
        remove(id);
      }
    };
  };

  const remove = (id) => {
    const index = toasts.value.findIndex(t => t.id === id);
    if (index !== -1) {
      toasts.value.splice(index, 1);
    }
  };

  const success = (text, actions?) => 
    show({ text, type: 'success', actionButtonsConfig: actions });

  const error = (text, actions?) => 
    show({ text, type: 'error', actionButtonsConfig: actions });

  const warning = (text, actions?) => 
    show({ text, type: 'warning', actionButtonsConfig: actions });

  const info = (text, actions?) => 
    show({ text, type: 'info', actionButtonsConfig: actions });

  return {
    toasts,
    show,
    remove,
    success,
    error,
    warning,
    info
  };
}

