export function createUndoToast(toastManager, config) {
  const { 
    message, 
    onUndo, 
    onCommit, 
    undoText = 'Undo',
    duration = 5000 
  } = config;

  let committed = false;

  // Show toast with undo button
  const { dismiss } = toastManager.show({
    text: message,
    type: 'success',
    duration,
    actionButtonsConfig: [
      {
        text: undoText,
        onClick: () => {
          onUndo();
          dismiss();
          committed = true;
        }
      }
    ]
  });

  // Commit after duration if not undone
  setTimeout(() => {
    if (!committed) {
      onCommit();
    }
  }, duration);
}

