implementationPatterns.code:
// useToast.ts
export function useToast() {
  const toasts = ref([]);

  const show = (text, type = 'light', options = {}) => {
    const id = Date.now();
    toasts.value.push({ id, text, type, ...options });

    setTimeout(() => {
      remove(id);
    }, options.duration || 3000);
  };

  const remove = (id) => {
    toasts.value = toasts.value.filter(t => t.id !== id);
  };

  return { toasts, show, remove };
}

// Usage:
const { show } = useToast();
show('Saved!', 'success');

const deleteWithUndo = (item) => {
  // Mark for deletion
  markedForDeletion.value = item;

  showToast.value = true;
  toastMessage.value = `${item.name} will be deleted`;

  // Actually delete after delay
  const timeoutId = setTimeout(() => {
    api.delete(item.id);
    markedForDeletion.value = null;
  }, 5000);

  // Undo handler
  const undo = () => {
    clearTimeout(timeoutId);
    markedForDeletion.value = null;
    showToast.value = false;
  };

  actionButtons.value = [{ text: 'Undo', onClick: undo }];
};

const toastQueue = ref([]);

const addToast = (toast) => {
  const id = Date.now();
  toastQueue.value.push({ ...toast, id });

  // Auto-remove
  setTimeout(() => {
    toastQueue.value = toastQueue.value.filter(t => t.id !== id);
  }, toast.duration || 3000);
};

// Template:
<div class="toast-stack">
  <InToasts
    v-for="toast in toastQueue"
    :key="toast.id"
    v-bind="toast"
  />
</div>

