select.handlingPattern:
@select="handleSelect"

handleSelect(option) {
  const isSelected = this.value.some(v => v.value === option.value);
  if (isSelected) {
    // Remove from selection
    this.value = this.value.filter(v => v.value !== option.value);
  } else {
    // Add to selection
    this.value = [...this.value, option];
  }
}

