/**
 * Usage examples for InButtonV2
 * These examples are used by:
 * - MCP server for code generation
 * - Documentation site
 * - AI assistants for implementation guidance
 */

export interface ComponentExample {
  id: string;
  title: string;
  description: string;
  category: 'basic' | 'variants' | 'states' | 'icons' | 'groups' | 'forms' | 'advanced';
  complexity: 'beginner' | 'intermediate' | 'advanced';
  code: string;
  props?: Record<string, any>;
  scriptSetup?: string;
  tags: string[];
  notes?: string;
}

export const examples: ComponentExample[] = [
  // ==================== BASIC ====================
  {
    id: 'basic-primary',
    title: 'Primary Button',
    description: 'Most common button variant for primary call-to-action',
    category: 'basic',
    complexity: 'beginner',
    code: `<InButtonV2 type="primary" label-text="Save Changes" />`,
    props: {
      type: 'primary',
      labelText: 'Save Changes'
    },
    tags: ['basic', 'primary', 'cta']
  },

  {
    id: 'basic-secondary',
    title: 'Secondary Button',
    description: 'For secondary actions or less prominent CTAs',
    category: 'basic',
    complexity: 'beginner',
    code: `<InButtonV2 type="secondary" label-text="Cancel" />`,
    props: {
      type: 'secondary',
      labelText: 'Cancel'
    },
    tags: ['basic', 'secondary']
  },

  {
    id: 'basic-danger',
    title: 'Danger Button',
    description: 'For destructive actions like delete, remove',
    category: 'basic',
    complexity: 'beginner',
    code: `<InButtonV2 type="danger" label-text="Delete" />`,
    props: {
      type: 'danger',
      labelText: 'Delete'
    },
    tags: ['basic', 'danger', 'destructive']
  },

  // ==================== VARIANTS ====================
  {
    id: 'variant-ghost',
    title: 'Ghost Button',
    description: 'Transparent background with border, lower visual weight',
    category: 'variants',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  styling="ghost"
  label-text="Learn More"
/>`,
    props: {
      type: 'primary',
      styling: 'ghost',
      labelText: 'Learn More'
    },
    tags: ['variant', 'ghost']
  },

  {
    id: 'variant-text',
    title: 'Text Button',
    description: 'No background or border, minimal visual weight',
    category: 'variants',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  styling="text"
  label-text="View Details"
/>`,
    props: {
      type: 'primary',
      styling: 'text',
      labelText: 'View Details'
    },
    tags: ['variant', 'text', 'minimal']
  },

  {
    id: 'variant-small',
    title: 'Small Size Button',
    description: 'Compact button for space-constrained UIs',
    category: 'variants',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  size="small"
  label-text="Apply"
/>`,
    props: {
      type: 'primary',
      size: 'small',
      labelText: 'Apply'
    },
    tags: ['variant', 'size', 'small']
  },

  // ==================== STATES ====================
  {
    id: 'state-disabled',
    title: 'Disabled Button',
    description: 'Prevent interaction, show disabled visual state',
    category: 'states',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  label-text="Submit"
  :disabled-status="!isFormValid"
/>`,
    props: {
      type: 'primary',
      labelText: 'Submit',
      disabledStatus: true
    },
    scriptSetup: `const isFormValid = ref(false);`,
    tags: ['state', 'disabled', 'validation']
  },

  {
    id: 'state-loading',
    title: 'Loading State',
    description: 'Show async operations in progress with spinner',
    category: 'states',
    complexity: 'intermediate',
    code: `<InButtonV2
  type="primary"
  label-text="Save"
  :loading-status="isLoading"
  @click="handleSave"
/>`,
    props: {
      type: 'primary',
      labelText: 'Save',
      loadingStatus: true
    },
    scriptSetup: `const isLoading = ref(false);

const handleSave = async () => {
  isLoading.value = true;
  try {
    await api.save();
  } finally {
    isLoading.value = false;
  }
};`,
    tags: ['state', 'loading', 'async']
  },

  {
    id: 'state-success',
    title: 'Success State',
    description: 'Briefly show success checkmark after operation completes',
    category: 'states',
    complexity: 'intermediate',
    code: `<InButtonV2
  type="primary"
  label-text="Saved"
  :success-status="showSuccess"
/>`,
    props: {
      type: 'primary',
      labelText: 'Saved',
      successStatus: true
    },
    scriptSetup: `const showSuccess = ref(false);

const handleSave = async () => {
  await api.save();
  showSuccess.value = true;
  setTimeout(() => {
    showSuccess.value = false;
  }, 2000);
};`,
    tags: ['state', 'success', 'feedback']
  },

  {
    id: 'state-selected',
    title: 'Selected State',
    description: 'Show button as active/pressed in button groups',
    category: 'states',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  label-text="Daily"
  :selected-status="currentView === 'daily'"
  @click="currentView = 'daily'"
/>`,
    props: {
      type: 'primary',
      labelText: 'Daily',
      selectedStatus: true
    },
    scriptSetup: `const currentView = ref('daily');`,
    tags: ['state', 'selected', 'toggle']
  },

  // ==================== ICONS ====================
  {
    id: 'icon-left',
    title: 'Button with Left Icon',
    description: 'Add icon to the left of label text',
    category: 'icons',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  label-text="Add Item"
  left-icon="filled-plus"
/>`,
    props: {
      type: 'primary',
      labelText: 'Add Item',
      leftIcon: 'filled-plus'
    },
    tags: ['icon', 'left']
  },

  {
    id: 'icon-right',
    title: 'Button with Right Icon',
    description: 'Add icon to the right of label text',
    category: 'icons',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  label-text="Next"
  right-icon="filled-arrow-right"
/>`,
    props: {
      type: 'primary',
      labelText: 'Next',
      rightIcon: 'filled-arrow-right'
    },
    tags: ['icon', 'right', 'navigation']
  },

  {
    id: 'icon-only',
    title: 'Icon-Only Button',
    description: 'Show only icon without label text',
    category: 'icons',
    complexity: 'beginner',
    code: `<InButtonV2
  type="primary"
  styling="ghost"
  left-icon="filled-settings"
  :label-text-status="false"
  tooltip-text="Settings"
/>`,
    props: {
      type: 'primary',
      styling: 'ghost',
      leftIcon: 'filled-settings',
      labelTextStatus: false,
      tooltipText: 'Settings'
    },
    tags: ['icon', 'icon-only', 'tooltip'],
    notes: 'Always provide tooltip-text for icon-only buttons for accessibility'
  },

  {
    id: 'icon-clickable',
    title: 'Separate Icon Click Handler',
    description: 'Handle icon clicks separately from button clicks',
    category: 'icons',
    complexity: 'intermediate',
    code: `<InButtonV2
  type="secondary"
  label-text="Tag Name"
  left-icon="filled-close"
  :icon-click="true"
  @click="handleTagClick"
  @clickIcon="handleRemoveTag"
/>`,
    props: {
      type: 'secondary',
      labelText: 'Tag Name',
      leftIcon: 'filled-close',
      iconClick: true
    },
    scriptSetup: `const handleTagClick = () => {
  console.log('Tag clicked');
};

const handleRemoveTag = () => {
  console.log('Remove icon clicked');
};`,
    tags: ['icon', 'clickable', 'events'],
    notes: 'Useful for removable tags or chips with separate actions'
  },

  // ==================== GROUPS ====================
  {
    id: 'group-basic',
    title: 'Button Group',
    description: 'Group related buttons together',
    category: 'groups',
    complexity: 'intermediate',
    code: `<InButtonV2
  type="primary"
  :button-group-options="[
    { id: 'daily', labelText: 'Daily' },
    { id: 'weekly', labelText: 'Weekly' },
    { id: 'monthly', labelText: 'Monthly' }
  ]"
  @click="handlePeriodChange"
/>`,
    props: {
      type: 'primary',
      buttonGroupOptions: [
        { id: 'daily', labelText: 'Daily' },
        { id: 'weekly', labelText: 'Weekly' },
        { id: 'monthly', labelText: 'Monthly' }
      ]
    },
    scriptSetup: `const handlePeriodChange = (payload) => {
  console.log('Selected period:', payload.id);
};`,
    tags: ['group', 'toggle']
  },

  // ==================== FORMS ====================
  {
    id: 'form-submit',
    title: 'Form Submission Button',
    description: 'Complete form example with validation and submission',
    category: 'forms',
    complexity: 'advanced',
    code: `<form @submit.prevent="handleSubmit">
  <InTextInput
    v-model="formData.email"
    label-text="Email"
    type="email"
    required
  />

  <InButtonV2
    type="primary"
    label-text="Submit"
    html-type="submit"
    :disabled-status="!isValid"
    :loading-status="isSubmitting"
  />
</form>`,
    props: {
      type: 'primary',
      labelText: 'Submit',
      htmlType: 'submit',
      disabledStatus: false,
      loadingStatus: false
    },
    scriptSetup: `const formData = reactive({
  email: ''
});

const isValid = computed(() => {
  return formData.email.length > 0 && formData.email.includes('@');
});

const isSubmitting = ref(false);

const handleSubmit = async () => {
  if (!isValid.value) return;

  isSubmitting.value = true;
  try {
    await api.submitForm(formData);
  } catch (error) {
    console.error('Form submission failed:', error);
  } finally {
    isSubmitting.value = false;
  }
};`,
    tags: ['form', 'validation', 'submit', 'advanced'],
    notes: 'Use html-type="submit" for form submission buttons'
  },

  // ==================== ADVANCED ====================
  {
    id: 'advanced-tooltip',
    title: 'Button with Custom Tooltip',
    description: 'Configure tooltip behavior and positioning',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InButtonV2
  type="primary"
  label-text="Export"
  tooltip-text="Download data as CSV"
  :tooltip-options="{
    id: 'export-tooltip',
    dynamicPosition: true,
    staticPosition: 'bottom center',
    offset: { x: 0, y: 8 }
  }"
/>`,
    props: {
      type: 'primary',
      labelText: 'Export',
      tooltipText: 'Download data as CSV',
      tooltipOptions: {
        id: 'export-tooltip',
        dynamicPosition: true,
        staticPosition: 'bottom center',
        offset: { x: 0, y: 8 }
      }
    },
    tags: ['advanced', 'tooltip', 'configuration']
  },

  {
    id: 'advanced-skeleton',
    title: 'Button with Skeleton Loader',
    description: 'Show skeleton placeholder during initial load',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InButtonV2
  type="primary"
  label-text="Save"
  :skeleton-status="isInitialLoading"
  skeleton-sizing="medium"
/>`,
    props: {
      type: 'primary',
      labelText: 'Save',
      skeletonStatus: true,
      skeletonSizing: 'medium'
    },
    scriptSetup: `const isInitialLoading = ref(true);

onMounted(async () => {
  await fetchInitialData();
  isInitialLoading.value = false;
});`,
    tags: ['advanced', 'skeleton', 'loading']
  }
];

/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InButtonV2 @click="handleClick()">Click</InButtonV2>`,
    right: `<InButtonV2 @click="handleClick">Click</InButtonV2>`,
    reason: 'No need for () in template event handlers unless passing arguments'
  },
  {
    wrong: `<InButtonV2 :disabled-status="disabledStatus === true">`,
    right: `<InButtonV2 :disabled-status="disabledStatus">`,
    reason: 'Boolean props don\'t need explicit comparison'
  },
  {
    wrong: `<InButtonV2 label-text="" />`,
    right: `<InButtonV2 label-text="Click Me" />`,
    reason: 'Always provide meaningful label text for accessibility'
  },
  {
    wrong: `<InButtonV2 left-icon="settings" :label-text-status="false" />`,
    right: `<InButtonV2 left-icon="settings" :label-text-status="false" tooltip-text="Settings" />`,
    reason: 'Icon-only buttons must have tooltip-text for accessibility'
  },
  {
    wrong: `<InButtonV2 type="primary" styling="solid" />`,
    right: `<InButtonV2 type="primary" />`,
    reason: 'styling="solid" is default, no need to specify explicitly'
  },
  {
    wrong: `<InButtonV2 type="danger" @click="deleteItem">`,
    right: `<InButtonV2 type="danger" @click="confirmDelete">`,
    reason: 'Always confirm destructive actions, don\'t delete directly'
  },
  {
    wrong: `<InButtonV2 :loading-status="true" :disabled-status="true">`,
    right: `<InButtonV2 :loading-status="true">`,
    reason: 'loading-status automatically disables the button'
  }
];

/**
 * Best practices for using InButtonV2
 */
export const bestPractices = [
  {
    practice: 'Use type="primary" for main CTAs',
    description: 'Primary buttons should be used for the most important action on a page',
    example: '<InButtonV2 type="primary" label-text="Save Changes" />'
  },
  {
    practice: 'Always provide tooltip-text for icon-only buttons',
    description: 'Screen readers and users benefit from descriptive tooltips',
    example: '<InButtonV2 left-icon="settings" :label-text-status="false" tooltip-text="Settings" />'
  },
  {
    practice: 'Use loading-status for async operations',
    description: 'Give users visual feedback during async operations',
    example: '<InButtonV2 :loading-status="isSubmitting" label-text="Save" />'
  },
  {
    practice: 'Confirm destructive actions',
    description: 'Never directly trigger delete/remove on button click',
    example: '@click="confirmDelete" instead of @click="deleteItem"'
  },
  {
    practice: 'Use html-type="submit" for form submissions',
    description: 'Enables native form submission and Enter key behavior',
    example: '<InButtonV2 html-type="submit" label-text="Submit" />'
  }
];

InButtonV2.typeDefs:
import { DefineComponent, ComputedRef } from 'vue';

/**
 * Button styling variants.
 * Defines the visual appearance of the button.
 */
export type ButtonStyling = 'solid' | 'ghost' | 'text';

/**
 * Button type variants.
 * Determines the color theme and semantic meaning.
 */
export type ButtonType =
  | 'primary'
  | 'subtle-primary'
  | 'secondary'
  | 'danger'
  | 'warning'
  | 'smart'
  | 'subtle-smart'
  | 'inverse';

/**
 * Button size variants.
 */
export type ButtonSize = 'default' | 'small';

/**
 * Valid icon sizes in pixels (as strings).
 */
export type IconSize = '40' | '24' | '20';

/**
 * Button styling variant constants.
 */
export declare const STYLES: {
  readonly SOLID: 'solid';
  readonly GHOST: 'ghost';
  readonly TEXT: 'text';
};

/**
 * Button type variant constants.
 */
export declare const TYPES: {
  readonly PRIMARY: 'primary';
  readonly SUBTLE_PRIMARY: 'subtle-primary';
  readonly SECONDARY: 'secondary';
  readonly DANGER: 'danger';
  readonly WARNING: 'warning';
  readonly SMART: 'smart';
  readonly SUBTLE_SMART: 'subtle-smart';
  readonly INVERSE: 'inverse';
};

/**
 * Button size variant constants.
 */
export declare const SIZES: {
  readonly DEFAULT: 'default';
  readonly SMALL: 'small';
};

/**
 * Valid icon sizes.
 */
export declare const ICON_SIZES: readonly ['40', '24', '20'];

/**
 * Configuration for a single button in a button group.
 */
export interface ButtonGroupOption {
  /**
   * Unique identifier for the button.
   */
  id: string;

  /**
   * Button type (color theme).
   */
  type: ButtonType;

  /**
   * Button styling variant.
   */
  styling: ButtonStyling;

  /**
   * Button label text.
   */
  labelText: string;

  /**
   * Whether this button is selected (active).
   * @default false
   */
  selectedStatus?: boolean;

  /**
   * Optional tooltip text for this button.
   * @default ''
   */
  tooltipText?: string;
}

/**
 * Tooltip configuration object.
 */
export interface TooltipOptions {
  /**
   * Tooltip element ID.
   */
  id: string;

  /**
   * Enable dynamic positioning.
   * @default false
   */
  dynamicPosition: boolean;

  /**
   * Static position when dynamicPosition is false.
   * @default 'top center'
   */
  staticPosition: string;

  /**
   * Show tooltip icon.
   * @default false
   */
  iconStatus: boolean;

  /**
   * Position offset.
   */
  offset: {
    x: number;
    y: number;
  };

  /**
   * Enable XSS protection.
   * @default false
   */
  preventXss: boolean;
}

/**
 * Default tooltip options constant.
 */
export declare const DEFAULT_TOOLTIP_OPTIONS: TooltipOptions;

/**
 * Skeleton loader sizing configuration.
 */
export interface SkeletonSizing {
  /**
   * Skeleton width in pixels.
   */
  width: number;

  /**
   * Skeleton height in pixels.
   */
  height: number;
}

/**
 * InButtonV2 component props.
 */
export interface InButtonV2Props {
  /**
   * Unique identifier for the button element.
   * Used for DOM manipulation, testing, and accessibility.
   *
   * @required
   * @example 'save-button'
   * @example 'user-profile-delete-btn'
   */
  id: string;

  /**
   * Visual styling variant of the button.
   * Determines the button's background and border appearance.
   *
   * @default 'solid'
   * @example 'solid' // Filled background with border (default)
   * @example 'ghost' // Transparent background with border
   * @example 'text' // No background or border (text only)
   */
  styling?: ButtonStyling;

  /**
   * Color theme variant of the button.
   * Determines the button's color palette and semantic meaning.
   *
   * @default 'primary'
   * @example 'primary' // Blue - main action button
   * @example 'danger' // Red - destructive actions (delete, remove)
   * @example 'secondary' // Grey - secondary actions
   * @example 'inverse' // White text on dark backgrounds
   */
  type?: ButtonType;

  /**
   * Button size variant.
   *
   * @default 'default'
   * @example 'default' // 40px height
   * @example 'small' // 32px height
   */
  size?: ButtonSize;

  /**
   * Disables the button, preventing user interaction.
   * Button will appear greyed out and not respond to clicks.
   *
   * @default false
   * @example :disabled-status="!isFormValid"
   * @example :disabled-status="isProcessing"
   */
  disabledStatus?: boolean;

  /**
   * Marks the button as selected (active state).
   * Typically used in button groups for toggle behavior.
   *
   * @default false
   * @example :selected-status="currentTab === 'overview'"
   */
  selectedStatus?: boolean;

  /**
   * Shows a loading spinner on the button.
   * Automatically disables the button while loading.
   *
   * @default false
   * @example :loading-status="isSubmitting"
   * @example :loading-status="isSaving"
   */
  loadingStatus?: boolean;

  /**
   * Shows a success checkmark icon on the button.
   * Typically displayed briefly after successful action completion.
   *
   * @default false
   * @example :success-status="saveSuccessful"
   */
  successStatus?: boolean;

  /**
   * Controls visibility of the label text.
   * When false, only icons will be displayed (if provided).
   *
   * @default true
   * @example :label-text-status="false" // Icon-only button
   */
  labelTextStatus?: boolean;

  /**
   * The text content displayed on the button.
   * Supports both strings and numbers.
   *
   * @default 'Label'
   * @example label-text="Save Changes"
   * @example label-text="Delete"
   * @example :label-text="itemCount" // Number example
   */
  labelText?: string | number;

  /**
   * Icon name to display on the left side of the label.
   * Icon names should match the InIcons component icon set.
   *
   * @default ''
   * @example left-icon="filled-plus"
   * @example left-icon="filled-trash"
   * @example left-icon="line-search"
   */
  leftIcon?: string;

  /**
   * Icon name to display on the right side of the label.
   * Icon names should match the InIcons component icon set.
   *
   * @default ''
   * @example right-icon="filled-dropdown-down"
   * @example right-icon="line-external-link"
   */
  rightIcon?: string;

  /**
   * Shows a skeleton loading placeholder instead of the button.
   * Used for initial page load states.
   *
   * @default false
   * @example :skeleton-status="isPageLoading"
   */
  skeletonStatus?: boolean;

  /**
   * Dimensions for the skeleton loader.
   * Only used when skeletonStatus is true.
   *
   * @default { width: 0, height: 0 }
   * @example :skeleton-sizing="{ width: 120, height: 40 }"
   */
  skeletonSizing?: SkeletonSizing;

  /**
   * Size of the icons in pixels (as string).
   *
   * @default '24'
   * @example icon-size="20" // Small icons
   * @example icon-size="24" // Default icons
   * @example icon-size="40" // Large icons
   */
  iconSize?: IconSize;

  /**
   * Configuration array for rendering a button group.
   * When provided, renders multiple buttons in a single component.
   *
   * @default []
   * @example
   * :button-group-options="[
   *   { id: 'daily', type: 'secondary', styling: 'solid', labelText: 'Daily', selectedStatus: true },
   *   { id: 'weekly', type: 'secondary', styling: 'ghost', labelText: 'Weekly', selectedStatus: false }
   * ]"
   */
  buttonGroupOptions?: ButtonGroupOption[];

  /**
   * Enables icon click events separate from button click.
   * When true, clicking the icon emits 'clickIcon' event instead of 'click'.
   *
   * @default false
   * @example :icon-click="true" @clickIcon="handleIconClick"
   */
  iconClick?: boolean;

  /**
   * Tooltip text displayed on button hover.
   * Shows a tooltip when provided.
   *
   * @default ''
   * @example tooltip-text="Save your changes"
   * @example tooltip-text="Delete this item permanently"
   */
  tooltipText?: string;

  /**
   * Configuration object for tooltip behavior.
   * Customizes tooltip positioning, styling, and behavior.
   *
   * @default DEFAULT_TOOLTIP_OPTIONS
   * @example
   * :tooltip-options="{
   *   id: 'custom-tooltip',
   *   dynamicPosition: true,
   *   staticPosition: 'bottom center',
   *   offset: { x: 0, y: 8 }
   * }"
   */
  tooltipOptions?: TooltipOptions;
  /**
   * Unique identifier for the button element.
   */
  id: string;
}

/**
 * Click event payload for single button.
 */
export interface SingleButtonClickPayload {}

/**
 * Click event payload for button group.
 */
export type ButtonGroupClickPayload = ButtonGroupOption;

/**
 * Event handler types for InButtonV2.
 * Use these types to type your event handlers.
 */
export interface InButtonV2EventHandlers {
  /**
   * Handler for click event.
   * For single buttons, payload is empty object {}.
   * For button groups, payload is the ButtonGroupOption of clicked button.
   *
   * @param payload - Empty object for single button, ButtonGroupOption for button group
   * @example
   * // Single button
   * const handleClick = (payload: SingleButtonClickPayload) => { ... }
   *
   * @example
   * // Button group
   * const handleClick = (payload: ButtonGroupClickPayload) => {
   *   console.log('Clicked:', payload.id);
   * }
   */
  onClick?: (payload: SingleButtonClickPayload | ButtonGroupClickPayload) => void;

  /**
   * Handler for icon click event (only when iconClick prop is true).
   * Used for buttons with separate icon and button click actions.
   *
   * @example
   * const handleIconClick = () => {
   *   console.log('Icon clicked');
   * }
   */
  onClickIcon?: () => void;
}

/**
 * InButtonV2 emits configuration (for Vue's EmitsOptions).
 */
export type InButtonV2Emits = {
  /**
   * Emitted when button is clicked.
   */
  click: (payload: SingleButtonClickPayload | ButtonGroupClickPayload) => true;

  /**
   * Emitted when icon is clicked (requires iconClick=true).
   */
  clickIcon: () => true;
};

/**
 * InButtonV2 Component
 *
 * Button component with multiple style variants and states.
 * Supports single buttons and button groups with loading, success, and disabled states.
 *
 * @example
 * // Basic usage
 * <InButtonV2
 *   id="save-btn"
