 *     { id: 'opt2', type: 'secondary', styling: 'ghost', labelText: 'Option 2', selectedStatus: false }
 *   ]"
 *   @click="handleGroupClick"
 * />
 *
 * @example
 * // TypeScript usage with event handlers
 * import type { InButtonV2EventHandlers } from './InButtonV2';
 *
 * const handlers: InButtonV2EventHandlers = {
 *   onClick: (payload) => console.log('Clicked:', payload),
 *   onClickIcon: () => console.log('Icon clicked')
 * };
 */
declare const InButtonV2: DefineComponent<InButtonV2Props>;

export default InButtonV2;

