      id="all-permissions"
      name="all-permissions"
      label-text="All Permissions"
      :bold-label="true"
      :checked="allSelected"
      :indeterminate="someSelected"
      @click="toggleAllPermissions"
    />

    <InCheckBoxV2
      id="permissions"
      name="permissions"
      :options="permissions"
      @click="handlePermissionChange"
    />
  </div>
</template>

<script setup>
import { ref, computed } from 'vue';

const permissions = ref([
  { text: 'Read', value: 'read', checked: true },
  { text: 'Write', value: 'write', checked: true },
  { text: 'Delete', value: 'delete', checked: false },
  { text: 'Admin', value: 'admin', checked: false }
]);

const allSelected = computed(() =>
  permissions.value.every(p => p.checked)
);

const someSelected = computed(() =>
  permissions.value.some(p => p.checked) && !allSelected.value
);

const toggleAllPermissions = (checked) => {
  permissions.value.forEach(p => p.checked = checked);
};

const handlePermissionChange = (selected) => {
  console.log('Selected permissions:', selected);
};
</script>`,
    scriptSetup: `const permissions = ref([
  { text: 'Read', value: 'read', checked: true },
  { text: 'Write', value: 'write', checked: true }
]);
const allSelected = computed(() => permissions.value.every(p => p.checked));`,
    tags: ['permissions', 'advanced', 'matrix', 'indeterminate']
  },

  {
    id: 'data-table-selection',
    title: 'Data Table Row Selection',
    description: 'Checkbox integration with data table for row selection',
    category: 'advanced',
    complexity: 'advanced',
    code: `<template>
  <div>
    <InCheckBoxV2
      id="select-all-rows"
      name="select-all-rows"
      label-text="Select all rows"
      :checked="allRowsSelected"
      :indeterminate="someRowsSelected"
      @click="toggleAllRows"
    />

    <div v-for="row in tableData" :key="row.id">
      <InCheckBoxV2
        :id="\`row-\${row.id}\`"
        :name="\`row-\${row.id}\`"
        :label-text="row.name"
        :checked="selectedRows.includes(row.id)"
        @click="toggleRow(row.id, $event)"
      />
    </div>

    <p>Selected: {{ selectedRows.length }} / {{ tableData.length }}</p>
  </div>
</template>

<script setup>
import { ref, computed } from 'vue';

const tableData = ref([
  { id: 1, name: 'Item 1' },
  { id: 2, name: 'Item 2' },
  { id: 3, name: 'Item 3' },
  { id: 4, name: 'Item 4' }
]);

const selectedRows = ref([1, 3]);

const allRowsSelected = computed(() =>
  selectedRows.value.length === tableData.value.length
);

const someRowsSelected = computed(() =>
  selectedRows.value.length > 0 && !allRowsSelected.value
);

const toggleAllRows = (checked) => {
  selectedRows.value = checked
    ? tableData.value.map(r => r.id)
    : [];
};

const toggleRow = (id, checked) => {
  if (checked) {
    selectedRows.value.push(id);
  } else {
    selectedRows.value = selectedRows.value.filter(r => r !== id);
  }
};
</script>`,
    scriptSetup: `const tableData = ref([
  { id: 1, name: 'Item 1' },
  { id: 2, name: 'Item 2' }
]);
const selectedRows = ref([1]);`,
    tags: ['table', 'selection', 'bulk-actions', 'advanced']
  }
];

export default examples;

