    wrong: `<InChips text="Tag" value="1" @click="handleClick()" />`,
    right: `<InChips text="Tag" value="1" @click="handleClick" />`,
    reason: 'Event handlers receive the value automatically, no need for ()'
  },
  {
    wrong: `<InChips text="Long text here" value="1" />`,
    right: `<InChips text="Long text here" value="1" :max-width="150" />`,
    reason: 'Use max-width to prevent layout issues with long text'
  },
  {
    wrong: `<InChips text="Tag" value="1" :interactive="false" :close-button="true" />`,
    right: `<InChips text="Tag" value="1" :interactive="false" :close-button="false" />`,
    reason: 'Close button is hidden when interactive is false'
  }
];

/**
 * Best practices for using InChips
 */
export const bestPractices = [
  {
    practice: 'Use semantic types for meaning',
    description: 'Choose type based on the semantic meaning of the tag',
    example: '<InChips text="Error" value="1" type="danger" />'
  },
  {
    practice: 'Set max-width for long text',
    description: 'Prevent layout breaks with truncation',
    example: '<InChips text="Long label" value="1" :max-width="150" />'
  },
  {
    practice: 'Use unique values',
    description: 'Ensure value prop is unique within the list for proper removal',
    example: 'value="tag-1" instead of value="1"'
  },
  {
    practice: 'Handle removal in parent',
    description: 'Manage chip list state in parent component',
    example: '@click="removeFromList" with proper state management'
  },
  {
