/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InDataTableV2 :columns="columns" :data="data" />`,
    right: `<InDataTableV2 id="my-table" :columns="columns" :data="data" />`,
    reason: 'id prop is required for proper table initialization'
  },
  {
    wrong: `<InDataTableV2 id="table" :columns="[]" :data="data" />`,
    right: `<InDataTableV2 id="table" :columns="columns" :data="data" />`,
    reason: 'columns array must not be empty'
  },
  {
    wrong: `const columns = [{ label: 'Name' }];`,
    right: `const columns = [{ key: 'name', label: 'Name' }];`,
    reason: 'key property is required for each column'
  },
  {
    wrong: `<InDataTableV2 id="table" :columns="columns" :data="undefined" />`,
    right: `<InDataTableV2 id="table" :columns="columns" :data="[]" />`,
    reason: 'data must be an array, not undefined'
  },
  {
    wrong: `{ key: 'status', label: 'Status', width: '100' }`,
    right: `{ key: 'status', label: 'Status', width: '100px' }`,
    reason: 'width must include CSS units (px, %, etc.)'
  }
];

/**
 * Best practices for using InDataTableV2
 */
export const bestPractices = [
  {
